[Form]
public class ProjControlPeriodTable extends FormRun implements ProjIControlPeriodTable
{
    ProjId                              projId;
    ProjType                            projType;
    ProjStatus                          projStatus;
    ProjControlId                       projControlId;

    QueryBuildRange                     activeAll;
    QueryBuildRange                     estimatePeriodIdRange;
    QueryBuildRange                     estimateDateRange;

    private boolean multipleContractLinesFeatureEnabled = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();
    private boolean contractLineBasedRevRecFeature = ProjContractLineBasedRevenueRecognitionFeatureHelper::isFeatureEnabled();
    boolean isContractLineBasedWIP;
}

void bookRefresh()
{
    projControlPeriodTable_DS.research();
    element.setButtonAcces();
    element.setFieldAccess();
}

void enableFieldsActive()
{
    if (!projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, LedgerDimension)).allowEdit(projControlPeriodTable.EstimateOutput == ProjEstimateOutput::Ledger))
    {
        projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, LedgerDimension)).setValue("");
    }
}

Args getArgs()
{
    Args    args    = new Args();
    ProjControlPeriodTable tmpProjControlPeriodTable;

    args.caller(element);
    if (projControlPeriodTable.RecId)
    {
        args.record(projControlPeriodTable);
    }
    else
    {
        tmpProjControlPeriodTable.ProjId = projControlPeriodTable.ProjId ? projControlPeriodTable.ProjId : element.projId();
        args.record(tmpProjControlPeriodTable);
    }

    return args;
}

void init()
{
    ProjTable                   projTable;
    ProjWIPTable                projWIPTable;
    ProjPeriodId                periodCode;
    ProjControlPeriodTable      projCtrlPeriodTable;
    PSAContractLineItems        contractLine;
    ProjRevRecContractLine      revRecContractLine;

    switch (element.args().dataset())
    {
        case(tableNum(ProjTable)):
            projTable = element.args().record();
            projWIPTable = ProjWIPTable::find(projTable.WIPProject);
            break;

        case(tableNum(ProjWIPTable)):
            projWIPTable   = element.args().record();
            break;

        case(tableNum(ProjControlPeriodTable)):
            projCtrlPeriodTable   = element.args().record();
            projWIPTable = ProjWIPTable::find(projCtrlPeriodTable.ProjId);
            break;
    }

    element.projId(projWIPTable.ProjId);
    element.projControlId(projWIPTable.ControlId);
    element.projType(projWIPTable.projTable().Type);
    element.projStatus(projWIPTable.projTable().Status);

    this.isContractLineBasedWIP(projWIPTable.IsContractLineUsed);

    if (element.contractLineBasedRevRecFeature() && element.isContractLineBasedWIP())
    {
        ProjRevRecHelper::prepareFixedPriceRevRecProjectWithRevProfileContractLine(projWIPTable);
    }

    else if (element.multipleContractLinesFeatureEnabled)
    {
        if (projTable && !projWIPTable)
        {
            throw error("@Proj:MissingFPRevRecProjectForEstimateCreation");
        }

        if (projTable && !projWIPTable.ProjRevenueProfileId)
        {
            boolean updatedSuccessfully = ProjRevRecHelper::updateFixedPriceRevRecProjectWithRevProfile(projWIPTable, false);

            if (!updatedSuccessfully)
            {
                throw error(strFmt("@Proj:FPRevRecProjectRevProfileAssociationFailure", projWIPTable.ProjId));
            }
        }
    }

    super();

    element.setDisplayLabel();
    element.setButtonAcces();
    element.setFieldAccess();

    if (projWIPTable)
    {
        periodCode = projWIPTable.PeriodId;
    }
    else
    {
        periodCode = (select firstOnly periodId from ProjWIPTable).PeriodId;
        ctrlEstimateDate.dateValue(ProjPeriodLine::findFromPeriod(periodCode, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())).PeriodTo);
    }
    ctrlEstimatePeriodId.text(periodCode);

    ProjMutilContractLinesFeatureFormController::updateFormControlVisibity(element, [Investment.id()]);

    if (FeatureStateProvider::isFeatureEnabled(ProjEstimatesToRevRecRenameFeature::instance()))
    {
        element.design().caption("@Proj:RevRec");
        ProjControlPeriod_PeriodTo.label("@Proj:RevRecDate");
        periodStatus.label("@Proj:RevenueStatus");
        ProjControlPeriod_EstimateProject.label("@Proj:RevenueProject");

        Administration_PeriodTo.label("@Proj:RevRecDate");
        Administration_PeriodStatus.label("@Proj:RevenueStatus");
        Administration_BegBal.helpText("@Proj:ProjRevRecBegBalCheckboxHelpTextV2");
    }
}

