void setButtonAcces()
{
    boolean         created;
    boolean         eliminated;
    boolean         lastPosted;
    ProjRevRecContractLine  revRecContractLine;
    PSAContractLineItems    contractline;
    boolean isProjEstimatesToRevRecRenameFeatureEnabled = FeatureStateProvider::isFeatureEnabled(ProjEstimatesToRevRecRenameFeature::instance());
    ctrlEliminateRev.enabled(false);

    if (!projControlPeriodTable.RecId)
    {
        element.setCtrlCreateAccess(true, isProjEstimatesToRevRecRenameFeatureEnabled);
        ctrlDelete.enabled(false);
        ctrlCalculate.enabled(false);
        ctrlPosting.enabled(false);
        ctrlReversal.enabled(false);
        element.setCtrlEditAccess(false, isProjEstimatesToRevRecRenameFeatureEnabled);
        ctrlLedger.enabled(false);

        if (ProjParameters::find().AllowEliminationWithoutEstimate &&
            ((contractLineBasedRevRecFeature && isContractLineBasedWIP) || this.projType() == ProjType::FixedPrice ||
             (element.multipleContractLinesFeatureEnabled && ProjMultipleContractLinesForProjectFeatureHelper::existContractLineForBillingMethod(this.projId(), ProjTable::find(this.projId()).ProjInvoiceProjId, ProjContractBillingMethod::FixedPrice))) &&
            ((contractLineBasedRevRecFeature && isContractLineBasedWIP && ProjRevRecHelper::getCompletePrincipleContractLine(ProjWIPTable::find(this.projId())) == ProjCompletePrincip::CompletedContract) || ProjRevRecHelper::getCompletePrincipleForProject(this.projId(), false) == ProjCompletePrincip::CompletedContract))
        {
            ctrlEliminate.enabled(true);
        }
        else
        {
            ctrlEliminate.enabled(false);
        }
    }
    else
    {
        if (element.args().dataset() == tableNum(ProjControlPeriodTable))
        {
            projControlPeriodTable   = element.args().record();
        }

        created     = ProjControlPeriodTable::created(ProjControlPeriodTable.ProjId, ProjControlPeriodTable.ControlId);
        eliminated  = ProjControlPeriodTable::eliminated(ProjControlPeriodTable.ProjId, ProjControlPeriodTable.ControlId);
        lastPosted  = ProjControlPeriodTable::lastPosted(ProjControlPeriodTable.ProjId, ProjControlPeriodTable.ControlId, projControlPeriodTable.RecId);
        if (! (created || eliminated))
        {
            element.setCtrlCreateAccess(true, isProjEstimatesToRevRecRenameFeatureEnabled);
        }
        else
        {
            element.setCtrlCreateAccess(false, isProjEstimatesToRevRecRenameFeatureEnabled);
        }

        if ((projControlPeriodTable.PeriodStatus  == ProjControlPeriodStatus::Created) && ! projControlPeriodTable.Elimination)
        {
            ctrlCalculate.enabled(true);
            ctrlDelete.enabled(true);
        }
        else
        {
            ctrlCalculate.enabled(false);
            ctrlDelete.enabled(false);
        }

        if ((projControlPeriodTable.PeriodStatus  == ProjControlPeriodStatus::Created) && ! projControlPeriodTable.Recalculated)
        {
            if (ProjControlPeriodCostGroup::errorTxt(projControlPeriodTable.ProjId,projControlPeriodTable.ControlId,projControlPeriodTable.Version,projControlPeriodTable.PeriodFrom))
            {
                ctrlPosting.enabled(false);
            }
            else
            {
                ctrlPosting.enabled(true);
            }
        }
        else
        {
            ctrlPosting.enabled(false);
        }

        if ((projControlPeriodTable.PeriodStatus  == ProjControlPeriodStatus::Posted && lastPosted  &&  ! (created || eliminated)))
        {
            ctrlReversal.enabled(true);
        }
        else
        {
            ctrlReversal.enabled(false);
        }

        if (projControlPeriodTable.Elimination)
        {
            if (ProjStatusTypeRule::find(this.projStatus(), this.projType(), ProjStatusRule::ReverseEliminate))
            {
                ctrlEliminateRev.enabled(true);
            }
            else
            {
                ctrlEliminateRev.enabled(false);
            }

            element.setCtrlEditAccess(true, isProjEstimatesToRevRecRenameFeatureEnabled);
        }
        else
        {
            element.setCtrlEditAccess(true, isProjEstimatesToRevRecRenameFeatureEnabled);
        }

        if (projControlPeriodTable.PeriodStatus == ProjControlPeriodStatus::Created)
        {
            ctrlLedger.enabled(false);
        }
        else
        {
            ctrlLedger.enabled(true);

            if (projControlPeriodTable.VoucherDatePosted)
            {
                ledgerTransVoucher_WIPPosted.enabled(true);
            }
            else
            {
                ledgerTransVoucher_WIPPosted.enabled(false);
            }
            if (projControlPeriodTable.VoucherDateReverse)
            {
                ledgerTransVoucher_WIPReverse.enabled(true);
            }
            else
            {
                ledgerTransVoucher_WIPReverse.enabled(false);
            }
        }

        if (projControlPeriodTable.PeriodStatus ==  ProjControlPeriodStatus::Posted
               && (!eliminated)
               && (!created))
        {
            ctrlEliminate.enabled(projControlPeriodTable.allowEliminate());
        }
        else
        {
            ctrlEliminate.enabled(false);
        }
    }

    if (this.projId())
    {
        boolean localAccess = false;
        ProjWIPTable wipProj = ProjWIPTable::find(this.projId());

        if (contractLineBasedRevRecFeature && wipProj.IsContractLineUsed)
        {
            localAccess = this.allowEstimateCreationContractLine(wipProj);
        }
        else
        {
            if (isProjEstimatesToRevRecRenameFeatureEnabled)
            {
                localAccess = ctrlCreateV2.enabled() && ProjTable::find(this.projId()).allowEstimateCreation();
            }
            else
            {
                localAccess = ctrlCreate.enabled() && ProjTable::find(this.projId()).allowEstimateCreation();
            }
        }

        element.setCtrlCreateAccess(localAccess, isProjEstimatesToRevRecRenameFeatureEnabled);
    }
    else
    {
        element.setCtrlCreateAccess(false, isProjEstimatesToRevRecRenameFeatureEnabled);
    }
}

private boolean allowEstimateCreationContractLine(ProjWIPTable _projWIPTable)
{
    boolean ret = false;
    if (!_projWIPTable.IsContractLineUsed)
    {
        throw error(strFmt("@SYS22828",funcName()));
    }
    if (_projWIPTable.ProjRevenueProfileId)
    {
        // Return true if there is no revenue profile associated with WIP project.
        ProjRevenueProfile revProf = ProjRevenueProfile::findByRevenueProfileId(_projWIPTable.ProjRevenueProfileId);
        ret = revProf && revProf.CompletePrinciple != ProjCompletePrincip::NoWIP;
    }
    return ret;
}

