void setFieldAccess()
{
    if ((contractLineBasedRevRecFeature && isContractLineBasedWIP) || element.projType() == ProjType::FixedPrice ||
        (element.multipleContractLinesFeatureEnabled && ProjMultipleContractLinesForProjectFeatureHelper::existContractLineForBillingMethod(this.projId(), ProjTable::find(this.projId()).ProjInvoiceProjId, ProjContractBillingMethod::FixedPrice)))
    {
        this.setFieldAccessWIP();
        this.setvisibleEstimateMeasures();
    }
    else
    {
        this.setFieldAccessInvestment();
        this.setFieldAccessOutput();
    }
}

void setFieldAccessInvestment()
{
    if(contractLineBasedRevRecFeature && isContractLineBasedWIP)
    {
        return;
    }
    element.setVisible(false);
    investment.visible(true);

    ProjTable projTableLoc = ProjTable::find(element.projId());
    ProjGroup projGroup;
    ProjRevenueProfile revProfile;

    if (element.multipleContractLinesFeatureEnabled)
    {
        revProfile = ProjRevRecHelper::getRevProfile(projTableLoc, ProjContractBillingMethod::FixedPrice, false);
        element.setFieldAccessTotalTabFromRevProfile(revProfile);
    }
    else
    {
        projGroup = ProjGroup::find(projTableLoc.ProjGroupId);
        element.setFieldAccessTotalTab(projGroup);
    }

    projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, AutoManualCalculation)).allowEdit(false);

    if ((projControlPeriodTable.PeriodStatus == ProjControlPeriodStatus::Posted)     ||
        (projControlPeriodTable.PeriodStatus == ProjControlPeriodStatus::Reversal    ||
        projControlPeriodTable.Elimination))
    {
        projControlPeriodtable_ds.allowEdit(false);
    }
    else
    {
        projControlPeriodTable_ds.allowEdit(true);
        plAccRevProfit1.allowEdit(false);
        wipSalespriceProfit1.allowEdit(false);
        plAccRevSalesValue1.allowEdit(false);
        wipSalespriceSalesValue1.allowEdit(false);
        projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, AutoManualCalculation)).allowEdit(true);

        plAccruedCost1.allowEdit(false);

        wipAccruedCost1.allowEdit(false);
        totalMaxCapital.allowEdit(false);

        if (element.multipleContractLinesFeatureEnabled)
        {
            element.enableWIPAndPLFields(revProfile.CompletePrinciple, revProfile.MatchingPrinciple);
            totalMaxCapital.allowEdit(revProfile.ForeseeableLosses == NoYes::Yes);
        }
        else
        {
            element.enableWIPAndPLFields(projGroup.CompletePrincip, projGroup.MatchingPrincip);
            totalMaxCapital.allowEdit(projGroup.ForeseablesLosses == NoYes::Yes);
        }

        if (projControlPeriodTable.PeriodStatus == ProjControlPeriodStatus::Created)
        {
            projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, AutoManualCalculation)).allowEdit(true);
        }

        if (projControlPeriodTable.AutoManualCalculation == ProjAutoManual::Manual)
        {
            projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, CompletePct)).allowEdit(true);
        }
    }
}

void setFieldAccessOutput()
{
    if(contractLineBasedRevRecFeature && isContractLineBasedWIP)
    {
        return;
    }
    investment.visible(true);

    projIdNew.allowEdit(false);
    categoryNew.allowEdit(false);
    activityNew.allowEdit(false);
    qtyCostNew.allowEdit(false);
    assetIdNew.allowEdit(false);
    assetBookId.allowEdit(false);
    ledgerAccountNew.allowEdit(false);
    projLinePropertyId.allowEdit(false);

    switch (projControlPeriodTable.EstimateOutput)
    {
        case ProjEstimateOutput::Ledger:
            ledgerAccountNew.allowEdit(true);
            break;

        case ProjEstimateOutput::Asset:
            assetIdNew.allowEdit(true);
            assetBookId.allowEdit(true);
            break;

        case ProjEstimateOutput::Project:
            projLinePropertyId.allowEdit(true);
            projIdNew.allowEdit(true);
            categoryNew.allowEdit(true);
            activityNew.allowEdit(true);
            qtyCostNew.allowEdit(true);
            break;
    }
}

void setFieldAccessTotalTabFromRevProfile(ProjRevenueProfile _revProfile)
{
    if(_revProfile && _revProfile.BillingMethod == ProjContractBillingMethod::FixedPrice)
    {
        element.setFieldAccessTotalTabFixedPrice();
    }
    else
    {
        element.setFieldAccessTotalTabNonFixedPrice(_revProfile.ForeseeableLosses);
    }
}

void setFieldAccessTotalTab(ProjGroup _projGroup)
{
    if (_projGroup.ProjType    == ProjType::FixedPrice)
    {
        element.setFieldAccessTotalTabFixedPrice();
    }
    else
    {
        element.setFieldAccessTotalTabNonFixedPrice(_projGroup.ForeseablesLosses);
    }
}

private void setFieldAccessTotalTabNonFixedPrice(NoYes _foreseeableLosses)
{
    if(contractLineBasedRevRecFeature && isContractLineBasedWIP)
    {
        return;
    }
    ContractValueGroup.visible(false);

    textFixedPrice2.visible(false);
    GrossMarginGroup.visible(false);
    NotCapitalizedGroup.visible(true);
    ToBeCapitalizedGroup.visible(true);
    MaximumCapitalizationGroup.visible(true);
    AccruedLossGroup.visible(true);

    totalMaxCapital.visible(true);
    if (_foreseeableLosses)
    {
        totalMaxCapital.visible(true);
