    totalTotalEstimateToBeCapital.visible(true);
    totalTotalEstimateNotCapital.visible(true);

    remainContract.visible(false);

    totalContract.visible(false);
    totalMargin.visible(false);
    totalEstimateAccLoss.visible(true);
}

private void setFieldAccessTotalTabFixedPrice()
{
        ContractValueGroup.visible(true);
        GrossMarginGroup.visible(true);

        NotCapitalizedGroup.visible(false);
        ToBeCapitalizedGroup.visible(false);
        MaximumCapitalizationGroup.visible(false);
        AccruedLossGroup.visible(false);

        totalMaxCapital.visible(false);
        totalTotalEstimateToBeCapital.visible(false);
        totalTotalEstimateNotCapital.visible(false);

        remainContract.visible(true);

        totalContract.visible(true);
        totalMargin.visible(true);
        totalEstimateAccLoss.visible(false);
}

void setFieldAccessWIP()
{
    boolean     isStraightLine;

    element.setVisible(true);
    investment.visible(false);

    ProjTable projTableLoc = ProjTable::find(element.projId());
    ProjWIPTable projWIPTableLoc = ProjWIPTable::find(element.projId());

    ProjGroup projGroup;
    ProjRevenueProfile revProfile;

    if(element.contractLineBasedRevRecFeature() && projWIPTableLoc.IsContractLineUsed)
    {
        revProfile = ProjRevenueProfile::findByRevenueProfileId(projWIPTableLoc.ProjRevenueProfileId);
        element.setFieldAccessTotalTabFromRevProfile(revProfile);
    }
    else if (element.multipleContractLinesFeatureEnabled)
    {
        revProfile = ProjRevRecHelper::getRevProfile(projTableLoc, ProjContractBillingMethod::FixedPrice, false);
        element.setFieldAccessTotalTabFromRevProfile(revProfile);
    }
    else
    {
        projGroup = ProjGroup::find(projTableLoc.ProjGroupId);
        element.setFieldAccessTotalTab(projGroup);
    }

    projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, AutoManualCalculation)).allowEdit(false);

    if ((projControlPeriodTable.PeriodStatus == ProjControlPeriodStatus::Posted)     ||
        (projControlPeriodTable.PeriodStatus == ProjControlPeriodStatus::Reversal    ||
        projControlPeriodTable.Elimination))
    {
        projControlPeriodtable_ds.allowEdit(false);
    }
    else
    {
        projControlPeriodTable_ds.allowEdit(true);
        plAccRevProfit1.allowEdit(false);
        wipSalespriceProfit1.allowEdit(false);
        plAccRevSalesValue1.allowEdit(false);
        wipSalespriceSalesValue1.allowEdit(false);
        projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, CompletePct)).allowEdit(false);

        plAccruedCost1.allowEdit(false);

        wipAccruedCost1.allowEdit(false);

        if (element.multipleContractLinesFeatureEnabled)
        {
            element.enableWIPAndPLFields(revProfile.CompletePrinciple, revProfile.MatchingPrinciple);
        }
        else
        {
            element.enableWIPAndPLFields(projGroup.CompletePrincip, projGroup.MatchingPrincip);
        }

        if (projControlPeriodTable.PeriodStatus == ProjControlPeriodStatus::Created)
        {
            projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, AutoManualCalculation)).allowEdit(true);
        }

        if (projControlPeriodTable.AutoManualCalculation == ProjAutoManual::Manual)
        {
            projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, CompletePct)).allowEdit(true);
        }

        // For StraightLine projects fields to block editing
        isStraightLine = ProjControl::isStraightLine(element.projId());
        projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, AutoManualCalculation)).allowEdit(!isStraightLine);
        projControlPeriodTable_ds.object(fieldNum(ProjControlPeriodTable, CompletePct)).allowEdit(!isStraightLine);
    }
}

private void enableWIPAndPLFields(ProjCompletePrincip _completePrinciple, ProjMatchingPrincip _matchingPrinciple)
{
    if (_completePrinciple == ProjCompletePrincip::CompletedPercentage)
    {
        if (_matchingPrinciple == ProjMatchingPrincip::ProductionProfit)
        {
            plAccRevProfit1.allowEdit(true);
            wipSalespriceProfit1.allowEdit(true);
        }
        else
        {
            plAccRevSalesValue1.allowEdit(true);
            wipSalespriceSalesValue1.allowEdit(true);
        }
    }
    else
    {
        plAccruedCost1.allowEdit(true);
        wipAccruedCost1.allowEdit(true);
    }
}

