void setvisibleEstimateMeasures()
{
    switch (ctrlEstimateType.selection())
    {
        case ProjEstimateColumn::Estimate:
            Group_EstimateMeasures.visible(true);
            Group_AccumulatedMeasures.visible(false);
            break;

        case ProjEstimateColumn::Accumulated:
            Group_EstimateMeasures.visible(false);
            Group_AccumulatedMeasures.visible(true);
            break;

        default:
            break;
    }
}

public boolean validateWrite()
{
    boolean ret = true;

    if (projControlPeriodTable.AssetIdNew != '' && projControlPeriodTable.AssetBookId == '')
    {
        ret = checkFailed("@FixedAssets:FA_MessageValidation_BookMustBeEnteredWhenEliminatingToAsset");
    }

    ret = ret && super();
    return ret;
}

edit ProjAccruedCost totalEstimatMaxCapital(
    boolean     set             = false,
    AmountMST   _totalMaxCapital = 0)
{
    ProjControlPeriodTableColumn    projControlColumnAccEstimatePrev
        = ProjControlPeriodTableColumn::findPrev(columnEstimat.ProjId,columnEstimat.ControlId,columnEstimat.PeriodFrom,ProjEstimateColumn::Accumulated,projControlPeriodTable.Elimination);

    AmountMST   totMaxCap           = projControlPeriodTable.totalEstimatMaxCapital(set,_totalMaxCapital);

    if (set)
    {
        columnEstimat.reCalcEstimate(ProjWIPType::Cost,totMaxCap,true);
    }

    return totMaxCap;
}

public void init()
{
    QueryBuildDataSource    qbds_periodTable;
    super();

    this.cacheAddMethod(tableMethodStr(ProjControlPeriodTable, displayPeriodStatus));

    this.query().dataSourceTable(tableNum(ProjControlPeriodTable)).clearDynalinks();

    if (element.projId())
    {
        this.query().dataSourceTable(tableNum(ProjControlPeriodTable)).addRange(fieldNum(ProjControlPeriodTable, ProjId)).value(element.projId());
    }

    qbds_periodTable = this.query().dataSourceTable(tableNum(ProjControlPeriodTable));
    activeAll = qbds_periodTable.addRange(fieldNum(ProjControlPeriodTable, PeriodStatus));
    estimatePeriodIdRange = qbds_periodTable.addRange(fieldNum(ProjControlPeriodTable, PeriodId));
    estimateDateRange = qbds_periodTable.addRange(fieldNum(ProjControlPeriodTable, PeriodTo));
}

public void executeQuery()
{
    TransDate   queryEndDate = dateMax();

    if (!ctrlIncludeReversal.checked())
    {
        activeAll.value(Global::queryValue(ProjControlPeriodStatus::Created) + ',' +
                            Global::queryValue(ProjControlPeriodStatus::Eliminate) + ',' +
                            Global::queryValue(ProjControlPeriodStatus::Posted));
    }
    else
    {
        activeAll.value('');
    }

    estimatePeriodIdRange.value(ctrlEstimatePeriodId.text());
    if (ctrlEstimateDate.dateValue() != dateNull())
    {
        estimateDateRange.value(queryValue(ctrlEstimateDate.dateValue()));
    }
    else
    {
        estimateDateRange.value(SysQuery::range(dateNull(), queryEndDate));
    }

    super();
}

void linkActive()
{
    ProjTable       projTable;
    ProjWIPTable    projWIPTable;
    ProjControlPeriodTable      projCtrlPeriodTable;

    super();

    switch (element.args().dataset())
    {
        case tableNum(ProjTable) :
            projTable = element.args().record();
            projWIPTable = ProjWIPTable::find(projTable.WIPProject);
            element.projId(projWIPTable.ProjId);
            element.projControlId(projWIPTable.ControlId);
            element.projType(projWIPTable.projTable().Type);
            break;

        case tableNum(ProjWIPTable) :
            projWIPTable = element.args().record();
            element.projId(projWIPTable.ProjId);
            element.projControlId(projWIPTable.ControlId);
            element.projType(projWIPTable.projTable().Type);
            break;

        case tablenum(ProjControlPeriodTable) :
            projCtrlPeriodTable = element.args().record();
            projWIPTable = ProjWIPTable::find(projCtrlPeriodTable.ProjId);
            element.projId(projWIPTable.ProjId);
            element.projControlId(projWIPTable.ControlId);
            element.projType(projWIPTable.projTable().Type);
            break;

        default:
            // Function '%1' has been used incorrectly.
            throw error(strFmt("@SYS19306", funcName()));
    }

    element.isContractLineBasedWIP(projWIPTable.IsContractLineUsed);

    element.setFieldAccess();
    element.setButtonAcces();
}

