        projControlPeriodTable.CompletePct              = projControlPeriodTable.completePct();
        projControlPeriodTable.CompletePctCalculated    = projControlPeriodTable.CompletePct;
    }
}

public void modified()
{
    super();

    if (projControlPeriodTable.CompletePct == 100)
    {
        if ((select firstonly projControlPeriodCostGroup
            where    projControlPeriodCostGroup.ProjId       == projControlPeriodTable.ProjId
            &&       projControlPeriodCostGroup.PeriodFrom   == projControlPeriodTable.PeriodFrom
            &&       projControlPeriodCostGroup.Version      == projControlPeriodTable.Version
            &&       projControlPeriodCostGroup.ctcCostAmount != 0).RecId)
        {
            projControlPeriodTable.CompletePctCalculated    = projControlPeriodTable.CompletePctCalculated;
        }
        else
        {
            projControlPeriodTable.CompletePctCalculated    = 100;
        }
    }
}

