public boolean modified()
{
    boolean ret = super();

    ProjOnAccTrans projOnAccTrans;
    ProjOnAccTransSale projOnAccTransSale;
    ProjTable projTable;
    ProjRevRecContractLine  revRecContractLine;

    if(element.contractLineBasedRevRecFeature() && element.isContractLineBasedWIP())
    {
        select sum(TotalSalesAmountCur) from projOnAccTrans
        exists join revRecContractLine
            where revRecContractLine.RevenueRecognitionId == element.projId()
            && revRecContractLine.ContractLineNum == projOnAccTrans.PSAContractLineNum
        exists join projOnAccTransSale
            where projOnAccTransSale.TransId == projOnAccTrans.TransId
                && projOnAccTransSale.TransStatus != ProjTransStatus::Adjusted;
    }
    else
    {            
    select sum(TotalSalesAmountCur) from projOnAccTrans
        exists join projTable
            where projTable.WIPProject == ProjControlPeriodTable.ProjId
                && projOnAccTrans.ProjID == projTable.ProjId
        exists join projOnAccTransSale
            where projOnAccTransSale.TransId == projOnAccTrans.TransId
                && projOnAccTransSale.TransStatus != ProjTransStatus::Adjusted;
    }

    Amount contractValue, accruedvalue;
    if (projOnAccTrans.TotalSalesAmountCur)
    {
        contractValue = projOnAccTrans.TotalSalesAmountCur;

        ProjControlPeriodTable projCtrlPeriodTable;
        select sum(WIPAccruedTurnover) from projCtrlPeriodTable
            where projCtrlPeriodTable.ProjId == element.projId();

        if (projCtrlPeriodTable.WIPAccruedTurnover)
        {
            accruedvalue = projCtrlPeriodTable.WIPAccruedTurnover;
        }
    }

    if (accruedvalue > contractValue)
    {
        if (FeatureStateProvider::isFeatureEnabled(ProjEstimatesToRevRecRenameFeature::instance()))
        {
            throw warning("@Proj:AccruedRevenueShouldNotExceedContractValueV2");
        }
        else
        {
            throw warning("@Proj:AccruedRevenueShouldNotExceedContractValue");
        }
    }

    return ret;
}
