public void close()
{
    FormRun caller;
    FormDataSource fds;

    if ( refreshCallerOnClose )
    {
        caller = element.args().caller();

        if ( caller )
        {
            fds = caller.dataSource(1);

            if ( fds )
            {
                fds.research(true);
            }
        }
    }
    super();
}

