str buildCaptionTextForActiveAcctItem()
{
    str activeAccountLabel;

    // Build the string with details of the current forcast expense item to use as label on the form caption
    activeAccountLabel = strFmt("%1 - %2: %3, %4", "@SYS342502", "@SYS82923" ,projForecastOnAcc.ModelId, date2StrUsr(projForecastOnAcc.TransDate, DateFlags::FormatAll));

    return activeAccountLabel;
}

int active()
{
    #SysSystemDefinedButtons

    int ret;
    boolean isBudgetForecast;

    ret = super();

    // Set the Form Caption with the caption text used for the active forcast OnAccount item
    element.design().caption(this.buildCaptionTextForActiveAcctItem());

    if (projForecastOnAcc.ProjForecastBudgetType == ProjForecastBudgetType::None)
    {
        isBudgetForecast = true;
    }

    boolean isExternallyManaged = Project::isProjectExternallyManaged(ProjForecastOnAcc.ProjId);
    boolean lockFieldsForForecasts = cdsLockingOptionForecasts && isExternallyManaged;

    projForecastOnAcc_ds.allowEdit(isBudgetForecast && !lockFieldsForForecasts);
    projForecastOnAcc_ds.allowDelete(isBudgetForecast && !lockFieldsForForecasts);
    ProjForecastCopy.enabled(!lockFieldsForForecasts);

    FormCommandButtonControl editButton = element.control(element.controlId(#SystemDefinedViewEditButton)) as FormCommandButtonControl;
    editButton.enabled(projForecastOnAcc_ds.allowEdit());

    return ret;
}

void linkActive()
{
    #define.star("*")

    Object caller;

    ProjInvoiceTable            projInvoiceTable;
    ProjWIPTable                projWIPTable;
    QueryBuildRange             rangeProjId;
    ProjTable                   projTable;
    ProjBudget                  projBudget;
    ProjBudgetRevision          projBudgetRevision;

    // Reset the Form Caption to just show the title of the form
    // If forcast OnAccount items exists for the current project, the Active method will be called
    // and it will update the caption to include details of the forcast OnAccount item
    element.design().caption("@SYS342502");

    caller = element.args().caller();

    switch (element.args().dataset())
    {
        case tableNum(ProjBudget):
            projBudget = element.args().record();
            projId = projBudget.RootProjId;
            if (caller && formHasMethod(caller, 'selectedProjId'))
            {
                projId = caller.selectedProjId();
            }
            break;

        case tableNum(ProjBudgetRevision):
            projBudgetRevision = element.args().record();
            projId = projBudgetRevision.rootProjId();
            if (caller && formHasMethod(caller, 'selectedProjId'))
            {
                projId = caller.selectedProjId();
            }
            break;

        case(tableNum(ProjTable)):
            projTable   = element.args().record();
            projId      = projTable.ProjId;
            break;

        case(tableNum(TmpProjStatistic)):
            // linkActive called again .criteria changed... close form
            if (projStatistic)
            {
                element.close();
            }

            allowEdit       = false;
            projStatistic   = element.args().parmObject();
            element.linkProjStatistic();
            break;

        case(tableNum(TmpProjCashFlow)):
            // linkActive called again .criteria changed... close form
            if (projCashFlow)
            {
                element.close();
            }

            allowEdit       = false;
            projCashFlow   = element.args().parmObject();
            element.linkProjCashFlow();
            break;

        case tableNum(ProjInvoiceTable):
            rangeProjId    = projForecastOnAcc_ds.query().dataSourceTable(tableNum(ProjForecastOnAcc)).findRange(fieldNum(ProjForecastOnAcc,ProjId));

            while (rangeProjId)
            {
                projForecastOnAcc_ds.query().dataSourceTable(tableNum(ProjForecastOnAcc)).clearRange(fieldNum(ProjForecastOnAcc,ProjId));
                rangeProjId    = projForecastOnAcc_ds.query().dataSourceTable(tableNum(ProjForecastOnAcc)).findRange(fieldNum(ProjForecastOnAcc,ProjId));
            }

            projInvoiceTable   = element.args().record();

            while select ProjId from projTable
                where projTable.ProjInvoiceProjId   == projInvoiceTable.ProjInvoiceProjId
            {
                projForecastOnAcc_ds.query().dataSourceTable(tableNum(ProjForecastOnAcc)).addRange(fieldNum(ProjForecastOnAcc,ProjId)).value(projTable.ProjId);
            }
            modelId = ProjParameters::find().ModelId;
            break;

        case tableNum(ProjWIPTable)     :
            rangeProjId    = projForecastOnAcc_ds.query().dataSourceTable(tableNum(ProjForecastOnAcc)).findRange(fieldNum(ProjForecastOnAcc,ProjId));

            while (rangeProjId)
            {
                projForecastOnAcc_ds.query().dataSourceTable(tableNum(ProjForecastOnAcc)).clearRange(fieldNum(ProjForecastOnAcc,ProjId));
                rangeProjId    = projForecastOnAcc_ds.query().dataSourceTable(tableNum(ProjForecastOnAcc)).findRange(fieldNum(ProjForecastOnAcc,ProjId));
            }

            projWIPTable   =   element.args().record();

            while select ProjId from projTable
                where projTable.wipProject  == projWIPTable.ProjId
            {
                projForecastOnAcc_ds.query().dataSourceTable(tableNum(ProjForecastOnAcc)).addRange(fieldNum(ProjForecastOnAcc,ProjId)).value(projTable.ProjId);
            }
            modelId = ProjParameters::find().ModelId;
            break;
    }

    criteriaProjId.value(projId + #star);

    super();
}

void initValue()
{
    projForecastOnAcc.ProjId   = projId;
    if (projId && cdsLockingOptionForecasts)
    {
        if(Project::isProjectExternallyManaged(projId))
        {
            projForecastOnAcc.ProjId = '';
        }
    }

    projForecastOnAcc.ModelId  = modelId;
    ProjTable projTable = ProjTable::find(projForecastOnAcc.ProjId);
    projForecastOnAcc.TransDate = projTable.returnForecastDate();
    projForecastOnAcc.initFromProjTable(projTable);

    super();
}

void init()
{
    Query                   q = new Query();
    QueryBuildDataSource    qb_projTable, qb_projForecastTrans;
    ProjForecastOnAcc       projForecastOnAccArg;

    super();

    projForecastOnAcc_ds.autoQuery(false);

    qb_projForecastTrans = q.addDataSource(tableNum(ProjForecastOnAcc));

    criteriaModelId   = qb_projForecastTrans.addRange(fieldNum(ProjForecastOnAcc, ModelId));
    criteriaTransDate = qb_projForecastTrans.addRange(fieldNum(ProjForecastOnAcc, TransDate));
    criteriaProjId    = qb_projForecastTrans.addRange(fieldNum(ProjForecastOnAcc, ProjId));
    criteriaRecId     = qb_projForecastTrans.addRange(fieldNum(ProjForecastOnAcc, RecId));

    qb_projTable      = qb_projForecastTrans.addDataSource(tableNum(ProjTable));
    qb_projTable.relations(true);

    criteriaProjInvoiceProjId = qb_projTable.addRange(fieldNum(ProjTable, ProjInvoiceProjId));
    criteriaProjId            = qb_projTable.addRange(fieldNum(ProjTable, ProjId));
    criteriaWIPProjId         = qb_projTable.addRange(fieldNum(ProjTable, wipProject));

    if (!element.enableProjMultipleContractLinesForProjectFeature)
    {                
        criteriaFixedPrice        = qb_projTable.addRange(fieldNum(ProjTable, Type));
        criteriaTimeMaterial      = qb_projTable.addRange(fieldNum(ProjTable, Type));
        criteriaInternal          = qb_projTable.addRange(fieldNum(ProjTable, Type));
        criteriaCost              = qb_projTable.addRange(fieldNum(ProjTable, Type));
        criteriaInvestment        = qb_projTable.addRange(fieldNum(ProjTable, Type));
    }
    criteriaSorting1          = qb_projTable.addRange(fieldNum(ProjTable, SortingId), 1);
    criteriaSorting2          = qb_projTable.addRange(fieldNum(ProjTable, SortingId), 2);
    criteriaSorting3          = qb_projTable.addRange(fieldNum(ProjTable, SortingId), 3);
    criteriaGroupId           = qb_projTable.addRange(fieldNum(ProjTable, ProjGroupId));
    criteriaCustAccount       = qb_projTable.addRange(fieldNum(ProjTable, CustAccount));

    if (element.args() && element.args().dataset() == tableNum(ProjForecastOnAcc))
    {
        projForecastOnAccArg = element.args().record();
        projId = projForecastOnAccArg.ProjId;
        criteriaRecId.value(queryValue(projForecastOnAccArg.RecId));
    }

    this.query(q);
}

