public class ProjGrant extends FormRun
{
    ProjGrantFormManager manager;
    NumberSeqFormHandler numberSeqFormHandler;

}

public ProjGrantFormManager initializeProjGrant(ProjGrant _projGrant)
{
    if (!manager)
    {
        manager = ProjGrantFormManager::construct(_projGrant);
    }
    else
    {
        manager.parmProjGrant(_projGrant);
    }

    return manager;
}

public NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm
        (ProjParameters::numRefGrantId().NumberSequenceId, element,  projGrant_DS,  fieldnum(ProjGrant, GrantId));
    }

    return numberSeqFormHandler;
}

public void updateDesign()
{
    boolean isSaved = projGrant.RecId ? true : false;
    boolean allowEdit = (projGrant.GrantStatus == ProjGrantStatus::Entered ||
                        projGrant.GrantStatus == ProjGrantStatus::Submitted ||
                        projGrant.GrantStatus == ProjGrantStatus::Approved);
    boolean isCustomerAccountRequired = projGrant.isCustomerRequired();

    invoiceControl.enabled(isSaved && projGrantView.ProjId);
    costControl.enabled(isSaved && projGrantView.ProjId);
    hourUtilization.enabled(isSaved && projGrantView.ProjId);
    cashFlow.enabled(isSaved && projGrantView.ProjId);
    projStatements.enabled(isSaved && projGrantView.ProjId);
    projPostedProjectTransactions_Grant.enabled(isSaved);

    projGrant_ds.object(fieldnum(ProjGrant, CustAccount)).mandatory(isCustomerAccountRequired);
    projGrant_ds.object(fieldnum(ProjGrant, CustAccount)).allowEdit((allowEdit ||
                                                                    (!projGrant.RecId &&
                                                                     (projGrant.GrantStatus == ProjGrantStatus::Awarded  ||
                                                                      projGrant.GrantStatus == ProjGrantStatus::Active)) ||
                                                                      (projGrant.RecId && !projGrant.CustAccount && isCustomerAccountRequired)
                                                                      ));

    element.enableDisableBasedOnViewMode();
    element.showFieldsBasedOnSEFAInquiryFeature();

    dirPartyContactInfoView_ds.executeQuery();
}

public int task(int _taskId)
{
    int ret;

    #Task

    ret = super(_taskId);

    switch (_taskId)
    {
        case #taskEditRecord :
        case #taskDoneEditRecord :
            if (ret)
            {
                element.enableDisableBasedOnViewMode();
            }
            break;

        default:
            // For all other tasks.
            break;
    }

    return ret;
}

/// <summary>
/// Enables actions strips based on the view mode of the document and customer account field
/// </summary>
private void enableDisableBasedOnViewMode()
{
    boolean inEditMode = !element.inViewMode();
    boolean isSaved = projGrant.RecId ? true : false;
    boolean hasCustomerAccount = projGrant.CustAccount ? true : false;

    // Button octions on contact action strip
    ButtonGroupContact.enabled(inEditMode && hasCustomerAccount);

    // Button actions on drawdown frequency action strip
    ButtonGroupFrequency.enabled(inEditMode && isSaved);

    // Button actions on Associated projects and contracts
    AssociatedProjectsButtonGroup.enabled(inEditMode);
    if (inEditMode)
    {
        element.enableDisableContractBasedButtons();
    }
}

public void init()
{
    super();

    element.enableDisableBasedOnViewMode();
}

/// <summary>
/// Enables contract related buttons based on grant status.
/// Grant are only allowed be added as a funding source when their status is active or awarded.
/// </summary>
private void enableDisableContractBasedButtons()
{
    boolean allowAddToContract = (projGrant.GrantStatus == ProjGrantStatus::Awarded  ||
                                  projGrant.GrantStatus == ProjGrantStatus::Active);

    ProjGrantFundingDialog.enabled(allowAddToContract);
    AddContract.enabled(allowAddToContract);
}

private void showFieldsBasedOnSEFAInquiryFeature()
{
    if (ProjSEFAInquiryFeature::isEnabled())
    {
        ProjGrant_CFDA.visible(true);

        FormStringControl existingCFDAId = element.control(element.controlId(formControlStr(ProjGrant, ProjGrant_CFDAId))) as FormStringControl;
        existingCFDAId.visible(false);

        GrpGrantorContactInfo_GrantorAgency.visible(true);
    }
}

