public void modified()
{
    ProjGrantCustomerContact projGrantCustomerContactLocal;
    super();
    if (projGrant.orig().CustAccount != projGrant.CustAccount)
    {
        ttsbegin;
        delete_from projGrantCustomerContactLocal where projGrantCustomerContactLocal.ProjGrant == projGrant.RecId;
        ttscommit;
        element.updateDesign();
    }
}

