public void init()
{
    super();
    this.query().dataSourceTable(tablenum(ProjGrantView)).addDynalink(fieldnum(ProjGrantView,GrantId),projGrant,fieldnum(ProjGrant,GrantId));
}

public void executeQuery()
{
    QueryBuildDataSource    projGrantCustomerContactDS;
    CustTable       custTable;

    custTable = CustTable::find(projGrant.CustAccount);
    DirPartyContactInfoView::updateQuery(this.query().dataSourceName(this.name()), custTable.Party);

    if (!this.query().dataSourceTable(tablenum(ProjGrantCustomerContact)))
    {
        projGrantCustomerContactDS = this.query().dataSourceTable(tablenum(DirPartyContactInfoView)).addDataSource(tablenum(ProjGrantCustomerContact));
    }
    else
    {
        projGrantCustomerContactDS = this.query().dataSourceTable(tablenum(ProjGrantCustomerContact));
    }

    projGrantCustomerContactDS.clearLinks();
    projGrantCustomerContactDS.clearRanges();
    projGrantCustomerContactDS.joinMode(JoinMode::ExistsJoin);
    projGrantCustomerContactDS.addLink(fieldnum(DirPartyContactInfoView, ElectronicAddress),
                            fieldnum(ProjGrantCustomerContact, LogisticsElectronicAddress));
    projGrantCustomerContactDS.addRange(fieldnum(ProjGrantCustomerContact, ProjGrant)).value(int642str(projGrant.RecId));

    super();
}

public void lookup()
{
    super();

    ProjGrantFormManager::lookupAllowableGrant(this,projGrant.GrantId);
}

public boolean modified()
{
    boolean ret = super();

    FormStringControl custAccountControl = element.control(element.controlId(formControlStr(ProjGrant, GrpGrantorContactInfo_CustAccount)));

    if (this.valueStr() == custAccountControl.valueStr())
    {
        warning("@Proj:GrantorAgencySameAsGrantCustomer");
    }

    return ret;
}

void clicked()
{
    ProjGrantCustomerContact projGrantCustomerContact;

    select forupdate * from projGrantCustomerContact where projGrantCustomerContact.ProjGrant == projGrant.RecId &&
                                                    projGrantCustomerContact.LogisticsElectronicAddress == dirPartyContactInfoView.ElectronicAddress;

    ttsbegin;
    projGrantCustomerContact.delete();
    ttscommit;
    element.updateDesign();

    super();
}

