    }
}

public void lookup()
{
    Systablelookup systablelookup = systablelookup::newparameters(tablenum(ProjCFDANumber), this);
    systablelookup.addlookupfield(fieldnum(ProjCFDANumber, CFDAID));
    systablelookup.addlookupfield(fieldnum(ProjCFDANumber, Description));

    Query query = new Query();
    Querybuilddatasource querybuilddatasource = query.adddatasource(tablenum(ProjCFDANumber));

    systablelookup.parmquery(query);
    systablelookup.performformlookup();
}

