public boolean modified()
{
    boolean ret;
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         criteriaTransStatus;
    QueryBuildRange         criteriaToBeInvoiced;

    ret = super();

    queryBuildDataSource = projPostTransView_1_ds.query().dataSourceTable(tableNum(ProjPostTransSaleView));
    criteriaTransStatus = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjPostTransSaleView, TransStatus));

    queryBuildDataSource = projPostTransView_1_ds.query().dataSourceTable(tableNum(ProjLineProperty));
    switch (this.selection())
    {
        case projSelectTrans::All:
            queryBuildDataSource.enabled(false);
            criteriaTransStatus.value(SysQuery::valueUnlimited());
            break;

        case projSelectTrans::Invoiced:
            queryBuildDataSource.enabled(false);
            criteriaTransStatus.value(strFmt('%1, %2, %3, %4, %5',
                                      queryValue(ProjTransStatus::Invoiced),
                                      queryValue(ProjTransStatus::Eliminated),
                                      queryValue(ProjTransStatus::Estimated),
                                      queryValue(ProjTransStatus::MarkedCreditnote),
                                      queryValue(ProjTransStatus::CreditnoteProposal)));

            break;

        case projSelectTrans::ToBeInvoiced:
            queryBuildDataSource.enabled(true);
            queryBuildDataSource.joinMode(JoinMode::NoExistsJoin);
            criteriaToBeInvoiced =  SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjLineProperty, ToBeInvoiced));
            criteriaToBeInvoiced.enabled(true);
            criteriaToBeInvoiced.value(SysQuery::value(NoYes::No));
            criteriaTransStatus.value(strFmt("%1,%2,%3",
                                      queryValue(ProjTransStatus::Registered),
                                      queryValue(ProjTransStatus::Posted),
                                      queryValue(ProjTransStatus::InvoiceProposal)));
            break;

        case projSelectTrans::NoChargeable:
            queryBuildDataSource.enabled(true);
            queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
            criteriaToBeInvoiced =  SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjLineProperty, ToBeInvoiced));
            criteriaToBeInvoiced.enabled(true);
            criteriaToBeInvoiced.value(SysQuery::value(NoYes::No));
            criteriaTransStatus.value(strFmt("%1",
                                      queryValue(ProjTransStatus::Posted)));
            break;

        default:
            //If nothing, do same as All
            queryBuildDataSource.enabled(true);
            criteriaTransStatus.value(SysQuery::valueUnlimited());
            break;
    }

    projPostTransView_1_ds.executeQuery();

    return ret;
}

public boolean modified()
{
    QueryBuildDataSource qbds;
    QueryFilter             queryFilter;
    boolean ret;

    ret = super();

    qbds = projPostTransView_1_ds.query().dataSourceTable(tableNum(ProjPostTransView));

    if (this.selection() == ProjGrantPostTransViewType::All)
    {
        projPostTransView_1_ds.query().clearQueryFilters();
    }
    else
    {
        queryFilter = projPostTransView_1_ds.query().findQueryFilter(qbds, fieldStr(ProjPostTransView, ProjTransType));
        if (!queryFilter)
        {
            queryFilter = projPostTransView_1_ds.query().addQueryFilter(qbds, fieldStr(ProjPostTransView, ProjTransType));
        }
        queryFilter.value(SysQuery::value(this.selection()));
    }

    projPostTransView_1_ds.executeQuery();

    return ret;
}
