[Form]
public class ProjGroup extends FormRun implements OfficeIMenuCustomizer
{
    // Member variable declarations
    ProjGroupId createprojGroupId;

    boolean enableMultipleContractLinesForProject = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();
    private boolean enabelProjProjectOperationsIntegration = ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();

}

public void AddRangeToLedgerDimensionAccountQuery(QueryBuildDataSource qbds,ProjAccountType paType)
{
    QueryBuildRange qbr;

    // There is a relation between ProjPosting and ProjCategoryGroup (one to many in that direction). Add
    // filters over the rest of the fields forming the primary key on ProjPosting to make the join 1:1.

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjFundingSource));
    qbr.value(SysQuery::value(0));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjAccountType));
    qbr.value(SysQuery::value(paType));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjCode));
    qbr.value(SysQuery::value(TableGroupAll::GroupId));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjRelation));
    qbr.value(SysQuery::value(projGroup.ProjGroupId));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm*/

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, CategoryCode));
    qbr.value(SysQuery::value(TableGroupAll::All));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjCategoryRelation));
    qbr.value(SysQuery::valueEmptyString());
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm*/
}

ProjGroupId  createprojGroupId(ProjGroupId   _createprojGroupId = createprojGroupId)
{
    createprojGroupId  = _createprojGroupId;

    return createprojGroupId;
}

/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    Set entitiesToExclude = element.entitiesToExcludeSet();
    Project::removeEntitiesFromOpenOfficeMenu(_menuOptions, entitiesToExclude);
}

/// <summary>
/// Gets entities excluded set.
/// </summary>
/// <returns>The set of entities to be excluded.</returns>
protected Set entitiesToExcludeSet()
{
    Set entitiesToExclude = new Set(Types::String);

    if (element.enableMultipleContractLinesForProject)
    {
        entitiesToExclude.add(dataentityviewstr(ProjLedgerPostingDefinitionEntity));
    }

    return entitiesToExclude;
}

public void enableFields()
{
    fixedPrice.allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditDataGroupFixedPrice());
    wipDefaults.allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditDataGroupWipDefaults());
    projgroup_ds.object(fieldNum(ProjGroup,ControlId)).mandatory(!element.enableMultipleContractLinesForProject && projGroup.allowEditDataGroupWipDefaults());
    projgroup_ds.object(fieldNum(ProjGroup,PeriodId)).mandatory(!element.enableMultipleContractLinesForProject && projGroup.allowEditDataGroupWipDefaults());
    journalizing.allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditDataGroupJournalizing());
    timeMaterial.allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditDataGroupTimeMaterial());

    ctrlCostTransCost.allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditDataMethodCostTransCostMethod());
    ctrlItemTransCost.allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditDataMethodItemTransCostMethod());

    projGroup_ds.object(fieldNum(ProjGroup, AccruedCostCategoryId)).allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditFieldAccruedCostCategoryId());
    projGroup_ds.object(fieldNum(ProjGroup, CostTransTurnover)).allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditFieldCostTransTurnover());
    projGroup_ds.object(fieldNum(ProjGroup, EmplTransCost)).allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditFieldEmplTransCost());
    projGroup_ds.object(fieldNum(ProjGroup, EmplTransTurnover)).allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditFieldEmplTransTurnover());
    projGroup_ds.object(fieldNum(ProjGroup, InvoicePosting)).allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditFieldInvoicePosting());
    projGroup_ds.object(fieldNum(ProjGroup, ForeseablesLosses)).allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditFieldForeseablesLosses());
    projGroup_ds.object(fieldNum(ProjGroup, ItemTransTurnover)).allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditFieldItemTransTurnover());
    projGroup_ds.object(fieldNum(ProjGroup, MatchingPrincip)).allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditFieldMatchingPrincip());
    projGroup_ds.object(fieldNum(ProjGroup, ProductionCategoryId)).allowEdit(!element.enableMultipleContractLinesForProject && projGroup.allowEditFieldProductionCategoryId());
