public void InitializeProjPostingDataSource(str projPostingdsName,ProjAccountType paType)
{
    ProjPosting projPosting;
    projPosting=this.datasource(projPostingdsName).cursor();
    projPosting.ProjFundingSource=0;
    projPosting.ProjAccountType = paType;
    projPosting.ProjCode = TableGroupAll::GroupId;
    projPosting.CategoryCode = TableGroupAll::All;
    projPosting.ProjCategoryRelation = '';
}

public void jumpRefInvStatus()
{
    new MenuFunction(menuitemDisplayStr(ProjLineProperty), MenuItemType::Display).run();
}

