    projGroup.onAccountAccount(true, projPosting_OnAccountAccount.LedgerDimension);
    projGroup.accruedRevenueOnAccount(true, projPosting_AccruedRevenueOnAccount.LedgerDimension);
    projGroup.wipInvoicedOnAccount(true, projPosting_WIPInvoicedOnAccount.LedgerDimension);
    projGroup.wipSalesvalue(true, projPosting_WIPSalesValue.LedgerDimension);
    projGroup.accruedTurnoverProd(true, projPosting_AccruedTurnoverProd.LedgerDimension);
    projGroup.wipProduction(true, projPosting_WIPProduction.LedgerDimension);
    projGroup.accruedTurnoverProfit(true, projPosting_AccruedTurnoverProfit.LedgerDimension);
    projGroup.wipProfit(true, projPosting_WIPProfit.LedgerDimension);
    projGroup.smaAccrueRevenueSubscription(true, projPosting_SMAAccruedRevenueSub.LedgerDimension);
    projGroup.smawipSubscription(true, projPosting_SMAWIPSubscription.LedgerDimension);

    // <GBR>
    projGroup.canceledInvoiceRevenue(true, ProjPosting_InvoiceCancelRevenueAccount.LedgerDimension);
    // </GBR>
}

public void initValue()
{
    DictEnum dictEnum = new DictEnum(enumNum(ProjType));
    int maxEnum = dictEnum.values();
    int i = 0;

    while (!isConfigurationkeyEnabled(dictEnum.value2ConfigurationKey(i)) &&
        i < maxEnum)
    {
        i += 1;
    }

    if (i == maxEnum) //all the the enum's config key are turned off
    {
        projGroup_ds.allowEdit(false);
    }
    else if (!element.enableMultipleContractLinesForProject)
    {
        projGroup.ProjType = i;
    }
    else
    {
        projGroup.projType = ProjType::None;
    }

    super();
}

int active()
{
    int ret;

    ret = super();

    element.enableFields();

    projPosting_CostAccount_ds.executeQuery();
    projPosting_PayrollAllocation_ds.executeQuery();
    projPosting_WIPCostValue_ds.executeQuery();
    projPosting_OffsetAccountItem_ds.executeQuery();
    projPosting_StatusAccountItem_ds.executeQuery();
    projPosting_AccruedCost_ds.executeQuery();
    projPosting_WIPCostAccrued_ds.executeQuery();

    projPosting_InvoiceTurnOver_ds.executeQuery();
    projPosting_accruedTurnoverSalesValue_ds.executeQuery();
    projPosting_OnAccountAccount_ds.executeQuery();
    projPosting_AccruedRevenueOnAccount_ds.executeQuery();
    projPosting_WIPInvoicedOnAccount_ds.executeQuery();
    projPosting_WIPSalesValue_ds.executeQuery();
    projPosting_accruedTurnoverProd_ds.executeQuery();
    projPosting_WIPProduction_ds.executeQuery();
    projposting_accruedTurnoverProfit_ds.executeQuery();
    projPosting_WIPProfit_ds.executeQuery();
    projPosting_SMAAccruedRevenueSub_ds.executeQuery();
    projPosting_SMAWIPSubscription_ds.executeQuery();

    // <GBR>
    ProjPosting_InvoiceCancelRevenueAccount_ds.executeQuery();
    // </GBR>

    return ret;
}

