public boolean modified()
{
    boolean ret;

    if ((ctrlProjLinePropertyId.text() && ! ProjLineProperty::checkExist(ctrlProjLinePropertyId.text())))
    {
        ctrlProjLinePropertyId.text('');
    }

    ret = super();

    return ret;
}

