public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret && !ProjPeriodTable::exist(default_projPeriodId.text()))
    {
        ret = checkFailed(strFmt("@SYS76877",default_projPeriodId.text()));
    }

    return ret;
}

