[Form]
public class ProjIntercompanyInvoice extends FormRun
{
    #define.LedgerJournalTable_CustPaym('LedgerJournalTable_CustPaym')
    #define.FormOpenModeForNew('FormOpenModeForNew')
    RefRecId        custInvoiceTouched;

