{
    custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, tdsGroup_IN)).enabled(!custInvoiceTable.tcsGroup_IN);
    custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, tcsGroup_IN)).enabled(!custInvoiceTable.tdsGroup_IN);
    CustInvoiceLineTaxExtensionIN_ds.object(fieldNum(CustInvoiceLineTaxExtensionIN, tdsGroup)).enabled(!custInvoiceLineTaxExtensionIN.tcsGroup);
    CustInvoiceLineTaxExtensionIN_ds.object(fieldNum(CustInvoiceLineTaxExtensionIN, tcsGroup)).enabled(!custInvoiceLineTaxExtensionIN.tdsGroup);
    withholdingTax_IN.enabled(custInvoiceLineTaxExtensionIN.tdsGroup || custInvoiceLineTaxExtensionIN.tcsGroup);
}

