boolean canClose()
{
    if (this.closedOk())
    {
        if (!projInvoiceParmTable.DocumentDate_W 
            && this.isDocumentDateMandatory())
        {
            return checkFailed(strFmt("@SYS26332", fieldPName(ProjInvoiceParmTable, DocumentDate_W)));
        }
        if (!projInvoiceParmTable.VatDueDate_W &&
            SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
        {
            return checkFailed(strFmt("@SYS26332", fieldPName(ProjInvoiceParmTable, VatDueDate_W)));
        }

        if (countryRegion_LTLV)
        {
            if (numbering_LT.visible() && ! numbering_LT.text())
            {
                return checkFailed(strFmt("@SYS26332",fieldPName(LtInvoiceAutoNumberingTable,NumberingCode)));
            }
            if (numbering_LT.visible() && ! LtInvoiceAutoNumberingTable::existUserNumbering(numbering_LT.text(),
                                                                                            DocNumberingModule_W::Project,
                                                                                            LtInvoiceAutoNumberingType::Invoice))
            {
                return checkFailed(strFmt("@GLS110719", numbering_LT.text(), curUserId()));
            }
            if (projInvoice_LT.allowEdit() && !projInvoice_LT.text())
            {
                return checkFailed(strFmt("@SYS26332", fieldPName(ProjInvoiceParmTable, ProjInvoiceId_LT)));
            }
        }
    }
    else
    {
        if (projFormLetter.editLinesChanged())
        {
            if (Box::yesNo("@SYS26912",DialogButton::Yes) != DialogButton::Yes)
            {
                return false;
            }
        }
    }

    return true;
}

void chooseTable()
{
    projFormLetter.editLinesChanged(true);

    projInvoiceParmTable_ds.executeQuery();
}

void closeOk()
{
    this.initProjFormLetter(projFormletter);

    if (projFormLetter.checkCloseDialog())
    {
        super();
    }
}

/// <summary>
/// Initialize <c>ProjFormLetter_Invoice</c> object.
/// </summary>
/// <param name = "_projFormletter"><c>ProjFormLetter_Invoice</c> instance.</param>
/// <returns>
/// Returns an initialized <c>ProjFormLetter_Invoice</c> object.
/// </returns>
protected projFormLetter_Invoice initProjFormLetter(ProjFormLetter _projFormletter)
{
    if (countryRegion_LTLV && numbering_LT.visible())
    {
        _projFormLetter.parmLtDocNumberingCode(numbering_LT.text());
    }

    boolean hasBillingRule = false;
    boolean isManaged      = false;
    hasBillingRule = PSAContractLineItems::hasBillingRules(projInvoiceParmtable.ProjInvoiceProjId);
    isManaged      = PSAProjInvoiceDetailLine::isManage(projInvoiceParmtable.ProposalId);

    _projFormletter.initParameters(updateNow.value(),
                                any2Enum(checkCreditMax.selection()),
                                invoiceDate.dateValue(),
                                storno.value(),
                                NoYes::No,
                                printout.selection(),
                                printFormLetter.value(),
                                usePrintManagement.value(),
                                hasBillingRule,
                                this.isRelease(),
                                isManaged);

    return _projFormletter;
}

void defaultGiroType()
{
    ProjProposalJour projProposalJour;

    select GiroType from projProposalJour where projProposalJour.ProposalId == projInvoiceParmTable.ProposalId;
    giroType.selection(projProposalJour.GiroType);
}

/// <summary>
/// Displays debit note lables.
/// </summary>
public void enableDebitNoteControls_BR()
{
    ProjProposalJour projProposalJour;

    if (BrazilParameters::isEnabled())
    {
        projProposalJour = ProjProposalJour::find(projInvoiceParmTable.ProposalId);
        if (projProposalJour.InvoiceType == ProjInvoiceType::DebitNote_BR)
        {
            element.design().caption("@GLS510043");
            projectInvoiceParamtable_InvoiceProposaleNum.label("@GLS3800182");
            projectInvoiceParamtable_InvoiceProposaleNum.helpText("@GLS510030");
            projectInvoiceParamtable_InvoiceAccount.label("@GLS510046");
            projectInvoiceParamtable_InvoiceAccount.helpText("@GLS510047");
            projInvoiceParmtable_Narration_BR.visible(true);
            invoiceDate.label("@GLS3800153");
            invoiceDate.helpText("@GLS3800177");
        }
    }
}

public Tax getShowTax_CZ()
{
    ProjProposalTotals projProposalTotals;
    projProposalTotals = new ProjProposalTotals(projInvoiceParmtable.projProposalJour_CZ(), projInvoiceParmTable.ParmId);
    projProposalTotals.calc();

    return projProposalTotals.tax();
}

void init()
{
    super();

    if (!element.args().caller())
    {
        throw error("@SYS22539");
    }

    lateSelect.value(0);

    projFormLetter  = element.args().caller();

    parmNum = projFormLetter.parmId();

    projInvoiceParmTable_ds.query().dataSourceTable(tableNum(ProjInvoiceParmTable)).addRange(fieldNum(ProjInvoiceParmTable,ParmId)).value(queryValue(parmNum));

    countryRegion_LTLV = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT, #isoLV]);
    if (countryRegion_LTLV)
    {
        numbering_LT.text(projFormLetter.parmLtDocNumberingCode());
    }

    this.initDesign_HU();
    this.initDesign_CZ();

    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        SalesTax.visible(false);
    }

    projInvoiceParmtable_ds.object(fieldNum(ProjInvoiceParmTable, VatDueDate_W)).visible(TaxParameters::isEasternEuropeOrVatDueDateFeatureEnabled());

    if (ProjPostingInvoiceDocumentDateFlight::instance().isEnabled()
        && this.isDocumentDateMandatory())
    {
        projInvoiceParmtable_ds.object(fieldNum(ProjInvoiceParmTable, DocumentDate_W)).mandatory(true);
    }
}

void initQuery(Query _lateSelectionQuery)
{
    if (_lateSelectionQuery)
    {
        const int maxControls = 10;

        if (_lateSelectionQuery.showInForm(LateSelectionGroup, maxControls))
        {
            pleaseMakeSelection.visible(false);
        }
        else
        {
            pleaseMakeSelection.visible(true);
        }
    }
    tabPageLateSelection.visible(true);
}

void initDesign_CZ()
{
    if (!isConfigurationkeyEnabled(configurationKeyNum(TradeDates_W)))
    {
        return;
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAll))
    {
        GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceEEU00013, funcName());
        // In Ax2009 this field was marked mandatory in the table meta data.
        projInvoiceParmtable_ds.object(fieldNum(ProjInvoiceParmTable,DocumentDate_W)).mandatory(true);
    }
}

void initDesign_HU()
{
    projInvoiceParmtable_VatDueDate_W.mandatory(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]));
}

private boolean isDocumentDateMandatory()
{
    return (isConfigurationkeyEnabled(configurationKeyNum(TradeDates_W))
        && SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAll));
}

private boolean isManaged()
{
    return ((select psaInvoiceFormats from projProposalJour where projProposalJour.ProposalId == projInvoiceParmTable.ProposalId).psaInvoiceFormats == PSAInvoiceFormats::ManageInvoice);
}

private boolean isRelease()
{
    return ProjProposalJour::isProposalRelease(projInvoiceParmTable.ProposalId);
}

void run()
{
    CustParameters custParameters = CustParameters::find();
    ProjFormLetter_Invoice projFormLetterInvoice = projFormLetter as ProjFormLetter_Invoice;

    updateNow.value             (!projFormLetter.proforma());
    printout.selection          (projFormLetter.printout());
    checkCreditMax.selection    (any2Int(custParameters.CreditMaxCheck));
    storno.value                (custParameters.CreditError);
    printFormLetter.value       (projFormLetter.printFormLetter());
    giroType.selection          (PaymentStub::None);
    usePrintManagement.value    (projFormLetter.usePrintManagement());

    if (projFormLetter.parmInBatch())
    {
        lateSelect.value(projFormLetter.parmLateSelection());

        if (projFormLetter.parmLateSelection() && projFormLetter.parmLateSelectionQuery())
        {
            projFormLetter.chooseLinesQuery(new SysQueryRun(projFormLetter.parmLateSelectionQuery()));
            element.initQuery(projFormLetter.chooseLinesQuery().query());
        }
    }

    this.setFieldAccess();

    super();
}

RunBase runBase()
{
    return projFormLetter;
}

void setFieldAccess()
{
    usePrintManagement.enabled(printFormLetter.value());
    printFormLetterButton.enabled(printFormLetter.value() && !usePrintManagement.value());

    if (!usePrintManagement.enabled())
    {
        usePrintManagement.value(enum2int(NoYes::No));
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]))
    {
        this.updateDesign_MX();
    }
}

