public int active()
{
    int ret;
    ProjProposalJour    projProposalJour;
    boolean             enableAutoNumbering;
    boolean             enableNumbering;
    if (countryRegion_LTLV)
    {
        projProposalJour = ProjProposalJour::find(projInvoiceParmTable.ProposalId);
        enableAutoNumbering = projProposalJour.InvoiceAutoNumbering_LT;
        enableNumbering = LtInvoiceAutoNumberingGroups::autoNumberingForProjInvoice(CustTable::find(projProposalJour.InvoiceAccount).CustGroup);
    }

    ret = super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        // If mandatory sales date entry is enabled, enforce the setting here
        boolean mandatorySalesDateEntry = TaxGroupHeading::find(projInvoiceParmTable.projProposalJour_CZ().TaxGroupId).MandatorySalesDate_W;

        projInvoiceParmtable_ds.object(fieldNum(ProjInvoiceParmTable, SalesDate_CZ)).mandatory(mandatorySalesDateEntry);

        if (ProjInvPropVatDueDate_W_MandatoryInUI_PLFlight::instance().isEnabled())
        {
            boolean mandatoryVatDueDate_W_DueToCustomer = ProjInvoiceJournalCreate::checkVatDueDate_W_ShouldBeMandatoryDueToCustTableMandatoryVatDate_PL(projInvoiceParmTable, false);
            projInvoiceParmtable_ds.object(fieldNum(ProjInvoiceParmTable, VatDueDate_W)).mandatory(mandatoryVatDueDate_W_DueToCustomer);
        }
    }

    element.defaultGiroType();

    if (countryRegion_LTLV)
    {
        projInvoice_LT.allowEdit((!enableAutoNumbering) && enableNumbering);

        numbering_LT.visible(enableAutoNumbering);
        ProjInvoiceParmtable.AutoNumbering_LT = enableAutoNumbering;
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(LedgerBasicVatDueDate_W)))
    {
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoEE, #isoLT, #isoLV, #isoPL]) || VatDueDateFeature::isEnabled())
        {
            projInvoiceParmtable_ds.object(fieldNum(ProjInvoiceParmTable,VatDueDate_W)).enabled(true);
        }
        else
        {
            if (!projProposalJour)
            {
                projProposalJour = ProjProposalJour::find(projInvoiceParmTable.ProposalId);
            }

            if (projProposalJour.CurrencyId == Ledger::accountingCurrency() ||
                CustParameters::find().EnableDateOfVATRegisterChanging_CZ   ||
                projInvoiceParmTable.checkCreditNote(projInvoiceParmTable.ProposalId))
            {
                projInvoiceParmtable_ds.object(fieldNum(ProjInvoiceParmTable,VatDueDate_W)).enabled(true);
            }
            else
            {
                projInvoiceParmtable_ds.object(fieldNum(ProjInvoiceParmTable,VatDueDate_W)).enabled(false);
            }
        }
    }
    if (BrazilParameters::isEnabled())
    {
        element.enableDebitNoteControls_BR();
    }

    textWarning.visible(ProjProposalJour::numOfRecords(projInvoiceParmTable.ProposalId) > 100);

    return ret;
}

