void  clicked()
{
    super();

    if (!lateSelect.value())
    {
        ProjInvoiceParmTable::deleteWithoutUpdate(parmNum);
    }

    ProjInvoiceSelect projInvoiceSelect = element.instantiateSelectedProjectInvoice();

    projFormLetter.parmLateSelection(lateSelect.value());

    if (projInvoiceSelect.run())
    {
        if (lateSelect.value())
        {
            projFormLetter.parmLateSelectionQuery(projInvoiceSelect.parmQueryUsed().pack());
            projFormLetter.pack();
            element.initQuery(projInvoiceSelect.parmQueryUsed());
        }
        else
        {
            lateSelect.value(0);
        }
        projInvoiceParmTable_DS.query().dataSourceTable(tableNum(ProjInvoiceParmTable)).addSortIndex(indexNum(ProjInvoiceParmTable, RecId));
        projInvoiceParmTable_DS.executeQuery();
    }

    element.chooseButtonClickedPost();
}

