public boolean validate()
{
    boolean ret = super();

    if (ret)
    {
        if (!this.text())
        {
            ret = checkFailed(strFmt("@SYS26332", fieldPName(LtInvoiceAutoNumberingTable, NumberingCode)));
        }
        else
        {
            if (!LtInvoiceAutoNumberingTable::existUserNumbering(this.text(),
                                                                DocNumberingModule_W::Project,
                                                                LtInvoiceAutoNumberingType::Invoice))
            {
                ret = checkFailed(strFmt("@GLS110719", this.text(), curUserId()));
            }
        }
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    projFormLetter.parmLateSelection(lateSelect.value());
    projInvoiceParmTable_ds.allowCreate(!lateSelect.value());
    projProposalJourDS_ds.allowCreate(!lateSelect.value());

    if (projFormLetter.lateSelectionRemoveLines(lateSelect.value(), parmNum))
    {
        projInvoiceParmTable_ds.executeQuery();
        TabPageLateSelection.visible(lateSelect.value());
    }
    else
    {
        ret = lateSelect.value(0);
    }

    return ret;
}

public boolean modified()
{
    boolean ret;
    ProjProposalJour    projProposalJour;
    SysQueryRun         chooseLines;
    boolean             multiform;

    ret = super();

    ttsbegin;

    delete_from projInvoiceParmTable where projInvoiceParmTable.ParmId == parmNum;

    chooseLines = new SysQueryRun(queryStr(ProjUpdate));
    if (ProjParameters::find().ApproveActive)
    {
        chooseLines.query().dataSourceTable(tableNum(ProjProposalJour)).addRange(fieldNum(ProjProposalJour, LineProperty)).value(queryValue(ProjLinePropertyCode::Approved));
    }
    else
    {
        chooseLines.query().dataSourceTable(tableNum(ProjProposalJour)).addRange(fieldNum(ProjProposalJour, LineProperty)).value(queryValue(ProjLinePropertyCode::Open));
    }
    chooseLines.query().interactive(false);

    while (chooseLines.next())
    {
        projProposalJour = chooseLines.get(tableNum(ProjProposalJour));

        if ((! ProjInvoiceParmTable::exist(parmNum, projProposalJour.ProjInvoiceId))
          && ((projProposalJour.GiroType == giroType.selection()) || giroType.selection() == PaymentStubInclAll::All))
        {
            projInvoiceParmTable.clear();
            projInvoiceParmTable.initFromProjProposalJour(projProposalJour);
            projInvoiceParmTable.ParmId                 = parmNum;
            projInvoiceParmTable.BatchAdministration    = multiform;
            projInvoiceParmTable.ParmJobStatus          = ParmJobStatus::Waiting;
            projInvoiceParmTable.insert();
        }
    }
    element.chooseTable();

    ttscommit;

    return ret;
}

