public void init()
{
    if (ProjFeatureStateHelper::isInvoiceFormsHeaderLineViewFeatureEnabled())
    {
        new MenuFunction(menuitemDisplayStr(ProjInvoiceJournalV2), MenuItemType::Display).run(element.args());
        this.close();
    }
    else
    {
        super();

        this.parseJumpToMainParms();

        generalFactorGrossMargin.label(ProjParameters::find().grossMarginDisplayLabel());
        generalFactorGrossMargin.helpText(ProjParameters::find().grossMarginHelpLabel());
        generalFactorGrossMargin.noOfDecimals(ProjParameters::find().GrossMarginDecimals);

        element.updateDesign(InventDimFormDesignUpdate::Init);

        ListGroup.visible(true);

        // <GTH>
        element.initDesign_TH();
        // </GTH>

        if (enabelProjProjectOperationsIntegration)
        {
            Functions.enabled(false);
            Functions.visible(false);

            MENUBUTTON.enabled(false);
            MENUBUTTON.visible(false);
        }
    }
}

