void setProjTableFilter(ProjId _projId)
{
    ProjInvoiceItem         invoiceItem;
    ProjInvoiceEmpl         invoiceEmpl;
    ProjInvoiceRevenue      invoiceRevenue;
    ProjInvoiceOnAcc        invoiceOnAcc;
    ProjInvoiceCost         invoiceCost;

    Set                     setFilter       = new Set(Types::String);
    SetIterator             itFilter;

    while select ProjInvoiceId from invoiceItem where invoiceItem.ProjId like _projId
    {
        setFilter.add(invoiceItem.ProjInvoiceId);
    }
    invoiceItem = null;

    while select ProjInvoiceId from invoiceEmpl where invoiceEmpl.ProjId like _projId
    {
        setFilter.add(invoiceEmpl.ProjInvoiceId);
    }
    invoiceEmpl = null;

    while select ProjInvoiceId from invoiceRevenue where invoiceRevenue.ProjId like _projId
    {
        setFilter.add(invoiceRevenue.ProjInvoiceId);
    }
    invoiceRevenue = null;

    while select ProjInvoiceId from invoiceOnAcc where invoiceOnAcc.ProjId like _projId
    {
        setFilter.add(invoiceOnAcc.ProjInvoiceId);
    }
    invoiceOnAcc = null;

    while select ProjInvoiceId from invoiceCost where invoiceCost.ProjId like _projId
    {
        setFilter.add(invoiceCost.ProjInvoiceId);
    }
    invoiceCost = null;

    QueryBuildDataSource qbds_ProjInvoiceJour = projInvoiceJour_ds.query().dataSourceTable(tableNum(ProjInvoiceJour));

    itFilter = new SetIterator(setFilter);
    while (itFilter.more())
    {
        qbds_ProjInvoiceJour.addRange(fieldNum(ProjInvoiceJour,ProjInvoiceId)).value(itFilter.value());
        itFilter.next();
    }

    if (setFilter.empty())
    {
        // do not select any data if nothing found
        qbds_ProjInvoiceJour.addRange(fieldNum(ProjInvoiceJour,ProjInvoiceId)).value(SysQuery::valueEmptyString());
    }
    else
    {
        //Filter on project will be applied only through the below conditions.
        QueryBuildDataSource  qbds_ProjTransPosting; 
        qbds_ProjTransPosting = qbds_ProjInvoiceJour.addDataSource(tableNum(ProjTransPosting));
        qbds_ProjTransPosting.joinMode(JoinMode::ExistsJoin);
        qbds_ProjTransPosting.addLink(fieldNum(ProjInvoiceJour, LedgerVoucher),fieldNum(ProjTransPosting, Voucher));
        qbds_ProjTransPosting.addLink(fieldNum(ProjInvoiceJour, InvoiceDate),fieldNum(ProjTransPosting, LedgerTransDate));
        qbds_ProjTransPosting.addRange(fieldNum(ProjTransPosting, ProjId)).value(_projId);
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    SalesLine       salesLine;
    InventMovement  movement = InventMovement::construct(salesLine);
    Set             set;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetup_Sales)
            {
                set = new Set(Types::String);
                set.add(inventDim_Sales_ds.name());
                inventDimFormSetup_Sales    = InventDimCtrl_Frm_Mov::newFromFormMovementDataSources(element, movement, set);
            }
            if (!inventDimFormSetup_Project)
            {
                set = new Set(Types::String);
                set.add(inventDim_Project_ds.name());
                inventDimFormSetup_Project  = InventDimCtrl_Frm_Mov::newFromFormMovementDataSources(element, movement, set);
            }
        // No break

        case InventDimFormDesignUpdate::Active      :
        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup_Sales.formSetControls(true);
            inventDimFormSetup_Project.formSetControls(true);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

void updateTab(str dsName)
{
    Map m;
    MapEnumerator me;

    m.insert(dsName,true);
    me = m.getEnumerator();
    while (me.moveNext())
    {
        if (! me.currentValue())
        {
            return;
        }
    }
}

