public void executeQuery()
{
    QueryBuildDataSource qbdsProjFundingSource;
    ProjGrant            projGrant;

    switch (element.args().dataset())
    {
        case tableNum(ProjGrant) :
            projGrant = element.args().record();
            qbdsProjFundingSource = this.query().dataSourceNo(1).addDataSource(tableNum(ProjFundingSource));
            qbdsProjFundingSource.addLink(fieldNum(ProjInvoiceJour,ProjInvoiceProjId),fieldNum(ProjFundingSource,ContractId));
            qbdsProjFundingSource.addRange(fieldNum(ProjFundingSource,ProjGrant)).value(queryValue(projGrant.RecId));
            break;

        case tableNum(SMAServiceOrderTable) :
            SMAServiceOrderTable    serviceOrder;
            ProjInvoiceLinesUnion   projInvoiceUnion;
            SMAServiceOrderLine     smaserviceOrderLine;
            boolean                 appliedFilter;

            serviceOrder = element.args().record();
            while select projInvoiceUnion
                group by projInvoiceUnion.ProjInvoiceId, projInvoiceUnion.InvoiceDate
                exists join smaserviceOrderLine
                where projInvoiceUnion.TransId          == smaserviceOrderLine.ProjTransId
                && smaserviceOrderLine.ServiceOrderId   == serviceOrder.ServiceOrderId
            {
                appliedFilter = true;
                this.query().dataSourceTable(tableNum(ProjInvoiceJour)).addRange(fieldNum(ProjInvoiceJour, ProjInvoiceId)).value(queryValue(projInvoiceUnion.ProjInvoiceId));
                this.query().dataSourceTable(tableNum(ProjInvoiceJour)).addRange(fieldNum(ProjInvoiceJour, InvoiceDate)).value(queryValue(projInvoiceUnion.InvoiceDate));
            }
            //Used when no filter is applied to prevent query from returning all the records, make it return nothing
            if (!appliedFilter)
            {
                this.query().dataSourceTable(tableNum(ProjInvoiceJour)).addRange(fieldNum(ProjInvoiceJour, ProjInvoiceId)).value(SysQuery::valueEmptyString());
            }
            break;
    }
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        if (element.args().menuItemName() == menuitemDisplayStr(ProjInvoiceJournal))
        {
            criteriaProjInvoiceType.value(queryValue(ProjInvoiceType::Invoice));
            criteriaProjInvoiceType1.value(queryValue(ProjInvoiceType::OnAccount));
        }
        if ((element.args().menuItemName() == menuitemDisplayStr(ProjDebitNoteJournal_BR)))
        {
            criteriaProjInvoiceType.value(queryValue(ProjInvoiceType::DebitNote_BR));
        }
    }
    // </GBR>

    super();
}

