        }
        functions.enabled(!projInvoiceRevenue.IsReleased);
    }

    return ret;
}

void linkActive()
{
    #define.star("*")

    ProjTable           projTable;
    ProjEmplTrans       projEmplTrans;
    ProjCostTrans       projCostTrans;
    ProjOnAccTrans      projOnAccTrans;
    ProjItemTrans       projItemTrans;
    ProjRevenueTrans    projRevenueTrans;
    ProjInvoiceJour     projInvoiceJourLocal;

    ProjInvoiceControl  projInvoiceControl;
    ProjId              projId;

    switch (element.args().dataset())
    {
        case(tableNum(ProjTable)):
            projTable = element.args().record();
            projId = queryValue(projTable.ProjId);
            projId += #star;
            element.setProjTableFilter(projId);
            break;

        // Project transactions - Hours
        case(tableNum(ProjEmplTrans)):
            criteriaProjInvoiceId.value(SysQuery::valueEmptyString());

            projEmplTrans = element.args().record();
            if (projEmplTrans.invoiceId())
            {
                criteriaProjInvoiceId.value(queryValue(projEmplTrans.invoiceId()));
            }
            break;

        // Project transactions - Cost
        case(tableNum(ProjCostTrans)):
            criteriaProjInvoiceId.value(SysQuery::valueEmptyString());

            projCostTrans = element.args().record();
            if (projCostTrans.invoiceId())
            {
                criteriaProjInvoiceId.value(queryValue(projCostTrans.invoiceId()));
            }
            break;

        // Project transactions - OnAccount
        case(tableNum(ProjOnAccTrans)):
            criteriaProjInvoiceId.value(SysQuery::valueEmptyString());

            projOnAccTrans = element.args().record();
            if (projOnAccTrans.invoiceId())
            {
                criteriaProjInvoiceId.value(queryValue(projOnAccTrans.invoiceId()));
            }
            break;

        // Project transactions - Revenue
        case(tableNum(ProjRevenueTrans)):
            criteriaProjInvoiceId.value(SysQuery::valueEmptyString());

            projRevenueTrans = element.args().record();
            if (projRevenueTrans.invoiceId())
            {
                criteriaProjInvoiceId.value(queryValue(projRevenueTrans.invoiceId()));
            }
            break;

        // Project transactions - Item
        case(tableNum(ProjItemTrans)):
            criteriaProjInvoiceId.value(SysQuery::valueEmptyString());

            projItemTrans = element.args().record();
            if (projItemTrans.invoiceId())
            {
                criteriaProjInvoiceId.value(queryValue(projItemTrans.invoiceId()));
            }
            break;

        case(tableNum(TmpProjInvoiceControl)):
            if (projInvoiceControl)
            {
                element.close();
            }
            projInvoiceControl  = element.args().parmObject();
            projId selectedProject = projInvoiceControl.parmProjId();

            if (selectedProject)
            {
                while select ProjInvoiceProjId from projTable
                    group by projTable.ProjInvoiceProjId
                    where projTable.ProjId like selectedProject
                {
                    this.queryBuildDataSource().addRange(fieldNum(ProjInvoiceJour,ProjInvoiceProjId)).value(projTable.ProjInvoiceProjId);
                }
            }
            else
            {
                criteriaInvoiceProjId.value(projInvoiceControl.parmProjInvoiceProjId());
            }
            break;

        // Project Invoice
        case(tableNum(ProjInvoiceJour)):
            //clear the range
            criteriaProjInvoiceId.value(SysQuery::valueEmptyString());

            // set the range
            projInvoiceJourLocal = element.args().record();
            criteriaProjInvoiceId.value(queryValue(projInvoiceJourLocal.ProjInvoiceId));
            break;
    }

    super();
}

