public int active()
{
    int ret;

    ret = super();

    boolean allowEdit = true;
    if (psaProjProposalProj.IsPreBill || ProjInvoiceJour.InvoiceAmount < 0)
    {
        allowEdit = false;
    }
    psaProjProposalProj_RetainedPercent.allowEdit(allowEdit);
    psaProjProposalProj_RetainedNow.allowEdit(allowEdit);

    return ret;
}

