///  <summary>
///  Cancel debit note proposals.
/// </summary>
void clicked()
{
    DialogButton                     diagBut;
    ProjInvoiceJour                  projInvoiceJourlocaL;
    Set                              set;
    str                              x;
    SysGlobalCache                   globalCache;
    Args                             args;
    ContainerClass                   conClass_Obj;
    container                        conSend;
    ProjProposalJour                 projProposalJour;
    LedgerJournalTrans               ledgerJournalTrans;
    FiscalCalendarPeriod             fiscalCalendarPeriod;
    LedgerFiscalCalendarPeriod       ledgerFiscalCalendarPeriod;
    // date                             cancelInvoiceDate;
    #define.one("One")
    #define.two("Two")

    super();
    args = new Args();

    projInvoiceJourlocaL        = projInvoiceJour_ds.getFirst(true);
    diagBut = Box::yesNo("@GLS510054",DialogButton::No,"@GLS3800180");

    if (diagBut == DialogButton::Yes)
    {
        globalCache = classfactory.globalCache();
        globalCache.set(curUserId(), 1, #one);
    }
    else
    {
        globalCache = classfactory.globalCache();
        globalCache.set(curUserId(), 1, #two);
        info("@GLS3800181");
    }
    globalCache = classfactory.globalCache();
    x           = globalCache.get(curUserId(), 1);

    if (x == #one)
    {
        set = new Set(Types::Record);

        for (projInvoiceJourlocaL = projInvoiceJour_ds.getFirst(true)
        ? projInvoiceJour_ds.getFirst(true)
        : projInvoiceJour_ds.cursor(); projInvoiceJourlocaL; projInvoiceJourlocaL = projInvoiceJour_ds.getNext())
        {
            set.add(projInvoiceJourlocaL);

            select StartDate, EndDate, RecId from fiscalCalendarPeriod
                join  FiscalCalendarPeriod, Status from ledgerFiscalCalendarPeriod
                    where fiscalCalendarPeriod.StartDate <= projInvoiceJourlocaL.InvoiceDate
                        && fiscalCalendarPeriod.EndDate >= projInvoiceJourlocaL.InvoiceDate
                        && ledgerFiscalCalendarPeriod.FiscalCalendarPeriod == fiscalCalendarPeriod.RecId
                        && ledgerFiscalCalendarPeriod.Ledger == Ledger::current();

            if (ledgerFiscalCalendarPeriod.Status == FiscalPeriodStatus::Closed || ledgerFiscalCalendarPeriod.Status == FiscalPeriodStatus::OnHold)
            {
                if (projInvoiceJourlocaL.ProjInvoiceType == ProjInvoiceType::DebitNote_BR)
                {
                    throw  error("@GLS510122");
                }
                else
                {
                    throw error("@GLS510002");
                }
            }
            select * from projProposalJour
                where projProposalJour.ProposalId == projInvoiceJourlocaL.ProposalId;

            if (projProposalJour.LineProperty==ProjLinePropertyCode::DebitNoteCanceled_BR)
            {
                throw error ("@GLS510057");
            }

            if (projProposalJour.LineProperty==ProjLinePropertyCode::DebitNote_BR && projProposalJour.InvoiceAmount < 0)
            {
                throw error ("@GLS510057");
            }
            select MarkedInvoice,SettleVoucher from ledgerJournalTrans
                where  ledgerJournalTrans.MarkedInvoice == projInvoiceJourlocaL.ProjInvoiceId;

            if (ledgerJournalTrans.SettleVoucher == SettlementType::SelectedTransact)
            {
                if ((!ProjParameters::find().SettledDebitnoteCancellation_BR == NoYes::Yes))
                {
                    throw error("@GLS510058");
                }
            }
            if (diagBut == DialogButton::Yes)
            {
                args.caller(this);
                args.record(projInvoiceJourlocaL);
                new MenuFunction(menuitemDisplayStr(ProjDebitNoteCancel_BR), MenuItemType::Display).run(args);
            }
            else
            {
                globalCache = classfactory.globalCache();
                globalCache.set(curUserId(), 1, #two);
                info("@GLS3800181");
            }
            conSend = conIns(conSend, 1,projInvoiceJourlocaL);
            conClass_Obj = new ContainerClass(conSend);
            args.parmObject(conClass_Obj);
        }
    }
}

///  <summary>
///  Cancel invoice proposals.
/// </summary>
void clicked()
{
    DialogButton                     diagBut;
    ProjInvoiceJour                  projInvoiceJourlocaL;
    Set                              set;
    str                              x;
    SysGlobalCache                   globalCache;
    Args                             args;
    ContainerClass                   conClass_Obj;
    container                        conSend;
    ProjProposalJour                 projProposalJour;
    LedgerJournalTrans               ledgerJournalTrans;
    LedgerFiscalCalendarPeriod       ledgerFiscalCalendarPeriod;
    FiscalCalendarPeriod             fiscalCalendarPeriod;

    #define.one("One")
    #define.two("Two")

    super();
    args = new Args();

    projInvoiceJourlocaL        = projInvoiceJour_ds.getFirst(true);
    diagBut = Box::yesNo("@GLS510000",DialogButton::No,"@GLS510011");

    if (diagBut == DialogButton::Yes)
    {
        globalCache = classfactory.globalCache();
        globalCache.set(curUserId(), 1, #one);
    }
    else
    {
        globalCache = classfactory.globalCache();
        globalCache.set(curUserId(), 1, #two);
        info("@GLS510012");
    }
    globalCache = classfactory.globalCache();
    x           = globalCache.get(curUserId(), 1);

    if (x == #one)
    {
        set = new Set(Types::Record);

        for (projInvoiceJourlocaL = projInvoiceJour_ds.getFirst(true)
        ? projInvoiceJour_ds.getFirst(true)
        : projInvoiceJour_ds.cursor(); projInvoiceJourlocaL; projInvoiceJourlocaL = projInvoiceJour_ds.getNext())
        {
            set.add(projInvoiceJourlocaL);

            select StartDate, EndDate, RecId from fiscalCalendarPeriod
                join  FiscalCalendarPeriod, Status from ledgerFiscalCalendarPeriod
                    where fiscalCalendarPeriod.StartDate <= projInvoiceJourlocaL.InvoiceDate
                        && fiscalCalendarPeriod.EndDate >= projInvoiceJourlocaL.InvoiceDate
                        && ledgerFiscalCalendarPeriod.FiscalCalendarPeriod == fiscalCalendarPeriod.RecId
                        && ledgerFiscalCalendarPeriod.Ledger == Ledger::current();

            if (ledgerFiscalCalendarPeriod.Status == FiscalPeriodStatus::Closed
               || ledgerFiscalCalendarPeriod.Status == FiscalPeriodStatus::OnHold)
            {
                if (projInvoiceJourlocaL.ProjInvoiceType == ProjInvoiceType::DebitNote_BR)
                {
                    throw  error("@GLS510122");
                }
                else
                {
                    throw error("@GLS510002");
                }
            }

            select * from projProposalJour
                where projProposalJour.ProposalId == projInvoiceJourlocaL.ProposalId;

            if (projProposalJour.LineProperty==ProjLinePropertyCode::InvoiceCanceled_BR)
            {
                throw error ("@GLS510013");
            }

            if (projProposalJour.LineProperty == ProjLinePropertyCode::Invoiced && projProposalJour.InvoiceAmount < 0)
            {
                throw error ("@GLS510013");
            }
            select MarkedInvoice,SettleVoucher from ledgerJournalTrans
                where  ledgerJournalTrans.MarkedInvoice == projInvoiceJourlocaL.ProjInvoiceId;

            if (ledgerJournalTrans.SettleVoucher == SettlementType::SelectedTransact)
            {
                if ((!ProjParameters::find().CancelSettleProjectInvoice_BR == NoYes::Yes))
                {
                    throw error("@GLS510014");
                }
            }
            if (diagBut == DialogButton::Yes)
            {
                args.caller(this);
                args.record(projInvoiceJourlocaL);
                new MenuFunction(menuitemDisplayStr(ProjInvoiceCancel_BR), MenuItemType::Display).run(args);
            }
            else
            {
                globalCache = classfactory.globalCache();
                globalCache.set(curUserId(), 1, #two);
                info("@GLS510012");
            }
            conSend = conIns(conSend, 1,projInvoiceJourlocaL);
            conClass_Obj = new ContainerClass(conSend);
            args.parmObject(conClass_Obj);
        }
    }
}

public void clicked()
{
    Args    args = new Args();
    Common  callerRecord;

    if (Item_Sales.isActivePage())
    {
        callerRecord = TaxEngineProjItemSOJourHeader::findOrCreateByProjInvoiceJour(projInvoiceJour);
        args.parm(classStr(TaxableDocumentDescriptorProjItemSOTrans));
    }
    else if (OnAccount.isActivePage())
    {
        callerRecord = TaxEngineProjOnAcctJourHeader::findOrCreateByProjInvoiceJour(projInvoiceJour);
        args.parm(classStr(TaxableDocumentDescriptorProjOnAcctTrans));
    }
    else if (RevenueSubscription.isActivePage())
    {
        callerRecord = TaxEngineProjRevenueSubJourHeader::findOrCreateByProjInvoiceJour(projInvoiceJour);
        args.parm(classStr(TaxableDocumentDescriptorProjRevenueSubTrans));
    }
    else if (Revenue.isActivePage())
    {
        callerRecord = TaxEngineProjRevenueJourHeader::findOrCreateByProjInvoiceJour(projInvoiceJour);
        args.parm(classStr(TaxableDocumentDescriptorProjRevenueTrans));
    }
    else if (Item_Project.isActivePage())
    {
        callerRecord = TaxEngineProjItemJourHeader::findOrCreateByProjInvoiceJour(projInvoiceJour);
        args.parm(classStr(TaxableDocumentDescriptorProjItemTrans));
    }
    else if (Cost.isActivePage())
    {
        callerRecord = TaxEngineProjCostJourHeader::findOrCreateByProjInvoiceJour(projInvoiceJour);
        args.parm(classStr(TaxableDocumentDescriptorProjCostTrans));
    }
    else if (Empl.isActivePage())
    {
        callerRecord = TaxEngineProjEmplJourHeader::findOrCreateByProjInvoiceJour(projInvoiceJour);
        args.parm(classStr(TaxableDocumentDescriptorProjEmplTrans));
    }

    args.record(callerRecord);
    new MenuFunction(menuitemActionStr(TaxDocumentLauncher), MenuItemType::Action).run(args);
}

