/// <summary>
///     This method sets the fields <c>RefInvoiceId</c>, <c>RefInvoiceAmountCur</c> and <c>ReasonTableRef</c>
///     of table <c>CustInvoiceTable</c> editable or not.
/// </summary>
/// <remarks>
///     These fields is not editable only when the note format is <c>TaxUnrealizedSalesNoteFormat::None</c>.
/// </remarks>
public void noteFormatEdit_TH()
{
    boolean     isNoneFormat;

    isNoneFormat = (ProjProposalJourExtension_TH.NoteFormat == TaxUnrealizedSalesNoteFormat::None);

    ProjProposalJourExtension_TH_ds.object(fieldNum(ProjProposalJour_TH, RefInvoiceId)).allowEdit(!isNoneFormat);
    ProjProposalJourExtension_TH_ds.object(fieldNum(ProjProposalJour_TH, RefInvoiceAmountCur)).allowEdit(!isNoneFormat);
    editReasonCodeForUnrealizedVAT_TH.allowEdit(!isNoneFormat);
}

