/// <summary>
/// Updates the controls used for selecting source invoice transaction.
/// </summary>
/// <param name="_projProposalMap_MY">
/// Map fore each kind of project proposal line
/// </param>
public void updateSourceRefControlsAfterLookup_MY(ProjProposalMap_MY _projProposalMap_MY)
{
    // <GMY>
    container invoiceInfoContainer;
    if (custInvoiceJourLookupController.parmInvoiceInfo())
    {
        invoiceInfoContainer = custInvoiceJourLookupController.parmInvoiceInfo();
        _projProposalMap_MY.RefInvoiceDate_MY = conPeek(invoiceInfoContainer, IndexOfRefInvoiceDate_MY);

        // After updated, clear the InvoiceInfo container of CustInvoiceJourLookupController to prepare for next lookup.
        custInvoiceJourLookupController.parmInvoiceInfo(conNull());
    }
    // </GMY>
}

