    element.parmProposalParams(proposalCreateLinesParams);
}

/// <summary>
/// Refreshes the Invoice summary grid when the transaction values change in the detail grid
/// </summary>
public void refreshSummaryGridSource()
{
    int     postion;

    // Get the position of record before refresh.
    postion = PSAProjProposalProj_DS.getPosition();

    PSAProjProposalProj_DS.executeQuery();

    // Set the position to the record which was selected before refresh.
    PSAProjProposalProj_DS.setPosition(postion);
}

/// <summary>
/// Sets fields visible when GTE feature is active.
/// </summary>
public void updateDesignGTE()
{
    if (ProjProposalOnAcc)
    {
        TaxDocumentLauncherOnAcctTrans.enabled(
        !TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(
            TaxEngineProjOnAcctTransHeader::findOrCreateByProjProposalJour(projProposalJour)).skipTaxDocument());
    }

    if (ProjProposalRevenue)
    {
        TaxDocumentLauncherRevenue.enabled(
        !TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(
            TaxEngineProjRevenueTransHeader::findOrCreateByProjProposalJour(projProposalJour)).skipTaxDocument());
    }

    if (ProjProposalCost)
    {
        TaxDocumentLauncherCost.enabled(
        !TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(
            TaxEngineProjCostTransHeader::findOrCreateByProjProposalJour(ProjProposalJour)).skipTaxDocument());
    }

    if (projProposalItem_Project)
    {
        TaxDocumentLauncherItem.enabled(
        !TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(
            TaxEngineProjItemTransHeader::findOrCreateByProjProposalJour(projProposalJour)).skipTaxDocument());
    }

    if (projProposalEmpl)
    {
        TaxDocumentLauncherEmpl.enabled(
        !TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(
            TaxEngineProjEmplTransHeader::findOrCreateByProjProposalJour(projProposalJour)).skipTaxDocument());
    }

    if (projProposalItem_SalesOrder)
    {
        TaxDocumentLauncherItemSO.enabled(
        !TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(
            TaxEngineProjItemSOTransHeader::findOrCreateByProjProposalJour(projProposalJour)).skipTaxDocument());
    }

    if (ProjProposalRevenueSubscription)
    {
        TaxDocumentLauncherRevenueSub.enabled(
        !TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(
            TaxEngineProjRevenueSubTransHeader::findOrCreateByProjProposalJour(projProposalJour)).skipTaxDocument());
    }
}

