/// Sets fields visible and changes the labels when Average VAT feature is active.
/// </summary>
private void updateDesign_TH()
{
    noteFormat_TH.visible(true);
    invoice_Reference_TH.visible(true);

    this.noteFormatEdit_TH();
}

public void close()
{
    this.updateInvoiceAmounts(projProposalJour);

    super();
}

protected PSAInvoiceFormat editInvoiceFormatForProjProposalJour(boolean _set, ProjProposalJour _projProposalJour, PSAInvoiceFormat _newValue)
{
    PSAInvoiceFormat    currentValue;
    PSAInvoiceFormats   formatValue;

    if (!isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        return currentValue;
    }

    select firstonly projProposalRevenuel where projProposalRevenuel.ProposalId == _projProposalJour.ProposalId;
    select firstonly RecId from psaProjInvoiceDetailLine where psaProjInvoiceDetailLine.ProposalId == projProposalJour.ProposalId;

    if (_set)
    {
        if (projProposalRevenuel && projProposalRevenuel.IsRetained)
        {
            if (_newValue == 0)
            {
                formatValue = PSAInvoiceFormats::DetailInvoice;
            }
        }
        else
        {
            if (contractHasBillingRules)
            {
                if (psaProjInvoiceDetailLine.RecId     != 0)
                {
                    switch (_newValue)
                    {
                        case 0:
                            formatValue = PSAInvoiceFormats::ManageInvoice;
                            break;

                        case 1:
                            formatValue = PSAInvoiceFormats::DetailInvoice;
                            break;
                    }
                }
                else
                {
                    switch (_newValue)
                    {
                        case 0:
                            formatValue = PSAInvoiceFormats::DetailInvoice;
                            break;
                        case 1:
                            formatValue = PSAInvoiceFormats::ManageInvoice;
                            break;
                    }
                }
            }
            else
            {
                if (psaProjInvoiceDetailLine.RecId     != 0)
                {
                    switch (_newValue)
                    {
                        case 0:
                            formatValue = PSAInvoiceFormats::ManageInvoice;
                            break;
                        case 1:
                            formatValue = PSAInvoiceFormats::DetailInvoice;
                            break;
                        case 2:
                            formatValue = PSAInvoiceFormats::SummaryInvoice;
                            break;
                        case 3:
                            formatValue = PSAInvoiceFormats::SummaryByCategory;
                            break;
                    }
                }
                else
                {
                    switch (_newValue)
                    {
                        case 0:
                            formatValue = PSAInvoiceFormats::DetailInvoice;
                            break;
                        case 1:
                            formatValue = PSAInvoiceFormats::SummaryInvoice;
                            break;
                        case 2:
                            formatValue = PSAInvoiceFormats::SummaryByCategory;
                            break;
                        case 3:
                            formatValue = PSAInvoiceFormats::ManageInvoice;
                            break;
                    }
                }
            }
        }

        if (_projProposalJour.psaInvoiceFormats != formatValue)
        {
            _projProposalJour.psaInvoiceFormats = formatValue;
            projProposalJour_ds.refresh();
        }
    }

    if (projProposalRevenuel && projProposalRevenuel.IsRetained)
    {
        if (_projProposalJour.psaInvoiceFormats == PSAInvoiceFormats::DetailInvoice)
        {
            currentValue = 0;
        }
    }
    else
    {
        if (contractHasBillingRules)
        {
            if (psaProjInvoiceDetailLine.RecId     != 0)
            {
                switch (_projProposalJour.psaInvoiceFormats)
                {
                    case PSAInvoiceFormats::DetailInvoice:
                        currentValue = 1;
                        break;
                    case PSAInvoiceFormats::ManageInvoice:
                        currentValue = 0;
                        break;
                }
            }
            else
            {
                switch (_projProposalJour.psaInvoiceFormats)
                {
                    case PSAInvoiceFormats::DetailInvoice:
                        currentValue = 0;
                        break;
                    case PSAInvoiceFormats::ManageInvoice:
                        currentValue = 1;
                        break;
                }
            }
        }
        else
        {
            if (psaProjInvoiceDetailLine.RecId     != 0)
            {
                switch (_projProposalJour.psaInvoiceFormats)
                {
                    case PSAInvoiceFormats::ManageInvoice:
                        currentValue = 0;
                        break;
                    case PSAInvoiceFormats::DetailInvoice:
                        currentValue = 1;
                        break;
                    case PSAInvoiceFormats::SummaryInvoice:
                        currentValue = 2;
                        break;
                    case PSAInvoiceFormats::SummaryByCategory:
                        currentValue = 3;
                        break;
                }
            }
            else
            {
                switch (_projProposalJour.psaInvoiceFormats)
                {
                    case PSAInvoiceFormats::DetailInvoice:
                        currentValue = 0;
                        break;
                    case PSAInvoiceFormats::SummaryInvoice:
                        currentValue = 1;
                        break;
                    case PSAInvoiceFormats::SummaryByCategory:
                        currentValue = 2;
                        break;
                    case PSAInvoiceFormats::ManageInvoice:
                        currentValue = 3;
                        break;
                }
            }
        }
    }
    return currentValue;
}

/// <summary>
/// Initializes the with tax payment field visible and editable.
/// </summary>
private void initWithTaxPayment_IN()
{
    TaxGSTEPZCodeHelper taxGSTEPZCodeHelper;
    FormDataObject withIGSTPayment;
    withIGSTPayment = projProposalJour_ds.object(fieldNum(ProjProposalJour, WithIGSTPayment_IN));

    if (projProposalItem_SalesOrder)
    {
        SalesTable salesTable = SalesTable::find(projProposalItem_SalesOrder.SalesId);
        taxGSTEPZCodeHelper = TaxGSTEPZCodeHelper::construct(salesTable);
    }
    else if (projProposalItem_Project)
    {
        taxGSTEPZCodeHelper = TaxGSTEPZCodeHelper::construct(projProposalJour, projProposalItem_Project);
    }
    else if (ProjProposalRevenue)
    {
        taxGSTEPZCodeHelper = TaxGSTEPZCodeHelper::construct(projProposalJour, ProjProposalRevenue);
    }
    else if (projProposalOnAcc)
    {
        taxGSTEPZCodeHelper = TaxGSTEPZCodeHelper::construct(projProposalJour, projProposalOnAcc);
    }
    else if (projProposalEmpl)
    {
        taxGSTEPZCodeHelper = TaxGSTEPZCodeHelper::construct(projProposalJour, projProposalEmpl);
    }
    else if (projProposalCost)
    {
        taxGSTEPZCodeHelper = TaxGSTEPZCodeHelper::construct(projProposalJour, projProposalCost);
    }
    else
    {
        taxGSTEPZCodeHelper = TaxGSTEPZCodeHelper::construct(projProposalJour);
    }
    withIGSTPayment.visible(taxGSTEPZCodeHelper.isEPZCodeNotNone() || taxGSTEPZCodeHelper.isExportOrder());
    withIGSTPayment.allowEdit(taxGSTEPZCodeHelper.isSEZ() || taxGSTEPZCodeHelper.isExportOrder());
}

/// <summary>
/// Sets the value of the tax with payment field.
/// </summary>
private void defaultValueWithTaxPayment_IN()
{
    TaxGSTEPZCodeHelper taxGSTEPZCodeHelper = TaxGSTEPZCodeHelper::construct(projProposalJour);
    projProposalJour.WithIGSTPayment_IN = taxGSTEPZCodeHelper.isEPZCodeNotNone();
}

/// <summary>
/// Initializes the would you claim refund field visible and editable.
/// </summary>
private void initWouldYouClaimRefund_IN()
{
    FormDataObject wouldYouClaimRefund;
    wouldYouClaimRefund = projProposalJour_ds.object(fieldNum(ProjProposalJour, WouldYouClaimRefund_IN));
    wouldYouClaimRefund.visible(projProposalJour.WithIGSTPayment_IN);
    wouldYouClaimRefund.allowEdit(projProposalJour.WithIGSTPayment_IN);
}

/// <summary>
/// Sets the value of the would you claim refund field.
/// </summary>
private void defaultValueWouldYouClaimRefund_IN()
{
    projProposalJour.WouldYouClaimRefund_IN = projProposalJour.WithIGSTPayment_IN;
}

private void openTaxInformationForm_IN(Common _record)
{
    using (TaxRecalculateProjProposalInvoiceAmountContext recalculateProjProposalInvoiceAmountContext = TaxRecalculateProjProposalInvoiceAmountContext::construct())
    {
        Args args = new Args(element);
        args.record(_record);

        MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(TransTaxInformation), MenuItemType::Display);

        FormRun taxInformationFormRun = menuFunction.create(args);
        taxInformationFormRun.run();
        taxInformationFormRun.wait();

        recalcInvoiceAmounts = recalculateProjProposalInvoiceAmountContext.parmShouldRecalculateInvoiceAmount();
    }
}

public void linkActive()
{
    if (callerRecord && callerRecord is PSATmpProjProposalTrans)
    {
        this.query().dataSourceTable(tableNum(ProjProposalJour)).clearDynalinks();
        rangeProjProposalId.value(element.getProposalIDRangeFromList());
    }

    super();
}

public void reread()
{
    super();

    element.fillInvoiceFormatCombo();
}

public void init()
{
    super();

    criteriaInvoiceProjId  = this.query().dataSourceNo(1).addRange(fieldNum(ProjProposalJour, ProjInvoiceProjId));
    rangeProjProposalId  = this.query().dataSourceNo(1).addRange(fieldNum(ProjProposalJour, ProposalId));
}

edit PSAInvoiceFormat editInvoiceFormat(boolean _set, ProjProposalJour _projProposalJour, PSAInvoiceFormat _newValue)
{
    return element.editInvoiceFormatForProjProposalJour(_set, _projProposalJour, _newValue);
}

void transactionRange()
{
    ProjCostTrans           projCostTransCaller;
    ProjCostTransSale       projCostTransSale;
    ProjEmplTrans           projEmplTransCaller;
    ProjEmplTransSale       projEmplTransSale;
    ProjItemTrans           projItemTransCaller;
    ProjItemTransSale       projItemTransSale;
    ProjOnAccTrans          projOnAccTransCaller;
    ProjOnAccTransSale      projOnAccTransSale;
    ProjRevenueTrans        projRevenueTransCaller;
    ProjRevenueTransSale    projRevenueTransSale;
    FormDataSource          formDataSource;
    ProjProposalId          tmpProjProposalId;

    proposalId              = SysQuery::valueEmptyString();
    if (FormDataUtil::getFormDataSource(element.args().record())) //if the calling record has a datasource then load the proposal id's from the calling record
    {
        switch (element.args().record().TableId)
        {
            case (tableNum(ProjCostTrans)):
                projCostTransCaller     = element.args().record();
                formDataSource          = FormDataUtil::getFormDataSource(projCostTransCaller);
                for (projCostTransCaller = formDataSource.getFirst(true) ?
                     formDataSource.getFirst(true) : formDataSource.cursor();
                     projCostTransCaller; projCostTransCaller = formDataSource.getNext())
                {
                    while select projCostTransSale
                        where projCostTransSale.TransId == projCostTransCaller.TransId
                    {
                        tmpProjProposalId = projCostTransSale.proposalId();
                        if (tmpProjProposalId)
                        {
                            if (proposalId == SysQuery::valueEmptyString())
                            {
                                proposalId = tmpProjProposalId;
                            }
                            else
                            {
                                proposalId = proposalId+','+tmpProjProposalId;
                            }
                        }
                    }
                }
                break;

            case (tableNum(ProjEmplTrans)):
                projEmplTransCaller     = element.args().record();
                formDataSource          = FormDataUtil::getFormDataSource(projEmplTransCaller);
                for (projEmplTransCaller = formDataSource.getFirst(true) ?
                     formDataSource.getFirst(true) : formDataSource.cursor();
                     projEmplTransCaller; projEmplTransCaller = formDataSource.getNext())
                {
                    while select projEmplTransSale
                        where projEmplTransSale.TransId == projEmplTransCaller.TransId
                    {
                        tmpProjProposalId = projEmplTransSale.proposalId();
                        if (tmpProjProposalId)
                        {
                            if (proposalId == SysQuery::valueEmptyString())
                            {
                                proposalId = tmpProjProposalId;
                            }
                            else
                            {
                                proposalId = proposalId+','+tmpProjProposalId;
                            }
                        }
                    }
                }
                break;

            case (tableNum(ProjRevenueTrans)):
                projRevenueTransCaller  = element.args().record();
                formDataSource          = FormDataUtil::getFormDataSource(projRevenueTransCaller);
                for (projRevenueTransCaller = formDataSource.getFirst(true) ?
                     formDataSource.getFirst(true) : formDataSource.cursor();
                     projRevenueTransCaller; projRevenueTransCaller = formDataSource.getNext())
                {
                    while select projRevenueTransSale
                        where projRevenueTransSale.TransId == projRevenueTransCaller.TransId
                    {
                        tmpProjProposalId = projRevenueTransSale.proposalId();
                        if (tmpProjProposalId)
                        {
                            if (proposalId == SysQuery::valueEmptyString())
                            {
                                proposalId = tmpProjProposalId;
                            }
                            else
                            {
                                proposalId = proposalId+','+tmpProjProposalId;
                            }
                        }
                    }
                }
                break;

            case (tableNum(ProjItemTrans)):
                projItemTransCaller     = element.args().record();
                formDataSource          = FormDataUtil::getFormDataSource(projItemTransCaller);
                for (projItemTransCaller = formDataSource.getFirst(true) ?
                     formDataSource.getFirst(true) : formDataSource.cursor();
                     projItemTransCaller; projItemTransCaller = formDataSource.getNext())
                {
                    while select projItemTransSale
                        where projItemTransSale.ProjTransId == projItemTransCaller.ProjTransId
                    {
                        tmpProjProposalId = projItemTransSale.proposalId();
                        if (tmpProjProposalId)
                        {
                            if (proposalId == SysQuery::valueEmptyString())
                            {
                                proposalId = tmpProjProposalId;
                            }
                            else
                            {
                                proposalId = proposalId+','+tmpProjProposalId;
                            }
                        }
                    }
                }
                break;

            case (tableNum(ProjOnAccTrans)):
                projOnAccTransCaller    = element.args().record();
                formDataSource          = FormDataUtil::getFormDataSource(projOnAccTransCaller);
                for (projOnAccTransCaller = formDataSource.getFirst(true) ?
                     formDataSource.getFirst(true) : formDataSource.cursor();
                     projOnAccTransCaller; projOnAccTransCaller = formDataSource.getNext())
                {
                    while select projOnAccTransSale
                        where projOnAccTransSale.TransId == projOnAccTransCaller.TransId
                    {
                        tmpProjProposalId = projOnAccTransSale.proposalId();
                        if (tmpProjProposalId)
                        {
                            if (proposalId == SysQuery::valueEmptyString())
                            {
                                proposalId = tmpProjProposalId;
                            }
                            else
                            {
                                proposalId = proposalId+','+tmpProjProposalId;
                            }
                        }
                    }
                }
                break;
        }
    }
}

public void write()
{
    super();

    if (TaxParameters::find().CashDiscOnInvoice)
    {
        if (projProposalJour.CashDiscCode &&
            projProposalJour.InvoiceAmount < 0)
        {
            info("@SYS99050");
        }
    }
}

void executeQuery()
{
    if (setTransactionRange)
    {
        this.transactionRange();
    }

    container applicableTables = [tableNum(BaseDocument_IT)];
    QueryLocalizationHelper::excludeLocalizationTablesIfApplicable(
        projProposalJour_DS.query(),
        applicableTables,
        element.name());

    super();

    if (projProposalJour)
    {
        contractHasBillingRules = PSAContractLineItems::hasBillingRules(projProposalJour.ProjInvoiceProjId);
    }
}

int active()
{
    int     ret;

    projFormLetter_Invoice.enabled(projProposalJour.enablePostButton());

    element.fillInvoiceFormatCombo();

    ret  = super();

    if (projProposalRevenue.IsReleased)
    {
        revenue.caption("@PSA11365");

        disableAddLinesButton = true;
    }

    element.setButtonAccess();
    element.setFieldAccess();

    if (CustTable::isCustDKPublic(projProposalJour.InvoiceAccount) == NoYes::Yes)
    {
        projProposalJour_ds.object(fieldNum(ProjProposalJour, eInvoiceLineSpecific)).visible(true);
        projProposalJour_ds.object(fieldNum(ProjProposalJour, eInvoiceAccountCode)).visible(true);
    }
    else
    {
        projProposalJour_ds.object(fieldNum(ProjProposalJour, eInvoiceLineSpecific)).visible(false);
        projProposalJour_ds.object(fieldNum(ProjProposalJour, eInvoiceAccountCode)).visible(false);
    }

    if (Project::Enabled())
    {
        if (projProposalJour.LineProperty == ProjLinePropertyCode::PSASubmitted
            || projProposalJour.LineProperty == ProjLinePropertyCode::Approved)               
        {
            projProposalJour_ds.allowEdit(true);
            psaProjProposalProj_RetainedNow.allowEdit(false);
            psaProjProposalProj_RetainedPercent.allowEdit(false);
            psaProjProposalProj_RetainedNow.enabled(false);
            psaProjProposalProj_RetainedPercent.enabled(false);
        }
        else if (projProposalJour.LineProperty == ProjLinePropertyCode::Invoiced
                 || projProposalJour.LineProperty == ProjLinePropertyCode::Canceled)
        {
            projProposalJour_ds.allowEdit(false);
            psaProjProposalProj_RetainedNow.allowEdit(false);
            psaProjProposalProj_RetainedPercent.allowEdit(false);
            psaProjProposalProj_RetainedNow.enabled(false);
            psaProjProposalProj_RetainedPercent.enabled(false);
        }
        else
        {
            projProposalJour_ds.allowEdit(true);
            psaProjProposalProj_RetainedNow.allowEdit(true);
            psaProjProposalProj_RetainedPercent.allowEdit(true);
            psaProjProposalProj_RetainedNow.enabled(true);
            psaProjProposalProj_RetainedPercent.enabled(true);
        }

        if (projProposalJour.LineProperty == ProjLinePropertyCode::Invoiced ||
            projProposalJour.LineProperty == ProjLinePropertyCode::Canceled)
        {
            psAbtPrintPreview.enabled(false);
        }
        else
        {
            psAbtPrintPreview.enabled(true);
        }
    }

    tabPageHeaderMiddle.visible(psaProjProposalProj ? true : false);

    // <GEEU>
    if (countryRegion_LTLV)
    {
        element.initDesign_LT();
    }
    // </GEEU>

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        element.enableDebitNoteControls_BR();
        dnProjFormLetter_Invoice_BR.enabled(projProposalJour.enablePostButton());
    }
    // </GBR>

    isCustPublic_NO = CustTable::isCustPublic_NO(projProposalJour.InvoiceAccount);

    // <GTH>
    if (TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled())
    {
        element.noteFormatEdit_TH();
    }
    // </GTH>

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.initWithTaxPayment_IN();
        element.initWouldYouClaimRefund_IN();
    }

    return ret;
}

/// <summary>
///     Gets and sets the reason code.
/// </summary>
/// <param name="set">
///     true if the reason code is set; otherwise, set to false.
/// </param>
/// <param name="_reasonCode">
///     The new reason code when <paramref name="set" /> is true.
/// </param>
/// <returns>
///     The reason code of the record in the <c>ReasonTableRef</c> table.
/// </returns>
public edit ReasonCode editReasonCodeForUnrealizedVAT_TH(boolean set, ReasonCode _reasonCode)
{
    ReasonCode                   reasonCodeLocal;
    ReasonCode                   reasonCode = strLRTrim(_reasonCode);
    RefRecId                     originalReasonRefRecID;
    FormDataSource               formSalesTable;

    if (set)
    {
        // If the ReasonRefRecID is 0, then create a new ReasonTableRef record
        // otherwise, update the existing record.
        if (projProposalJourExtension_TH.ReasonTableRef == 0)
        {
            projProposalJourExtension_TH.ReasonTableRef = ReasonTableRef::createFromCode(reasonCode);
        }
        else
        {
            if (reasonCode)
            {
                //The reasonCode value is not blank.
                // Save the current ReasonRefRecID for comparision.
                originalReasonRefRecID  = projProposalJourExtension_TH.ReasonTableRef;
                projProposalJourExtension_TH.ReasonTableRef     = ReasonTableRef::updateCode(reasonCode, projProposalJourExtension_TH.ReasonTableRef);

                if (projProposalJourExtension_TH.ReasonTableRef == originalReasonRefRecID)
                {
                    // The reason code has changed but the referenced record ID has not.
                    // Get the current FormDataSource and set forceWrite() to true so that
                    // data validation (validateWrite()) is performed on the updated reason code.
                    this.forceWrite(true);
                }
            }
            else
            {
                // The reasonCode is blank.
                projProposalJourExtension_TH.ReasonTableRef = ReasonTableRef::updateCode(reasonCode, projProposalJourExtension_TH.ReasonTableRef);
            }
        }
    }
    else
    {
        reasonCode = ReasonTableRef::find(projProposalJourExtension_TH.ReasonTableRef).Reason;
    }

    reasonCodeLocal = reasonCode;
    return reasonCodeLocal;
}

