public int active()
{
    //boolean             adjustmentsAllowed;
    ProjEmplTransSale   projEmplTransSale;
    int                 ret;

    ret = super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        psaIndirectComponents.enabled(projEmplTrans.psaIndirectComponentGroup ? true : false);
    }

    projEmplTransSale = ProjEmplTransSale::find(projProposalEmplDetail.SaleRefRecId);
    //The prodAdjustmentEmpl drop dialog is hidden based on late pre-release feedback.  Looking to revisit in future release.
    //adjustmentsAllowed = ProjTrans::newProjEmplTransSale(projEmplTransSale.projEmplTrans(), projEmplTransSale).adjustable();
    //projAdjustmentEmpl.enabled(adjustmentsAllowed);

    if (projProposalEmpl_ds.allowEdit())
    {
        projProposalEmpl_ds.object(fieldNum(ProjProposalEmpl, eInvoiceAccountCode)).enabled(projProposalJour.eInvoiceLineSpecific || isCustPublic_NO);
    }

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        element.updateDesignGTE();
    }
    // </GTE>

    return ret;
}

public void linkActive()
{
    super();

    if (!calledForEditInGrid && projProposalJour_ds.recordsMarked().lastIndex() <= 1)
    {
        empl.visible(projProposalEmpl    ? true : false);

        projProposalEmpl_EInvoiceAccountCode.visible(CustTable::isCustDKPublic(projProposalJour.InvoiceAccount));

        if (projProposalEmpl && jumpToMain && parmProjTransType == ProjTransType::Hour)
        {
            projProposalEmpl_ds.positionToRecord(ProjProposalEmpl::find(parmTransId, projProposalJour.ProposalId));
            projProposalEmpl_TransId.setFocus();
        }
    }
    else
    {
        empl.visible(false);
    }

    emplgrid.update();
}

public void write()
{
    EInvoiceAccountCode eInvoiceAccountCodeOrig;

    if (isCustPublic_NO)
    {
        eInvoiceAccountCodeOrig = projProposalEmpl.orig().EInvoiceAccountCode;
    }

    super();

    if (isCustPublic_NO && projProposalEmpl.EInvoiceAccountCode != eInvoiceAccountCodeOrig)
    {
        projProposalJour.setEInvoiceAccountCodeFromLines_NO();
        projProposalJour.update();

        ProjProposalJour_DS.reread();
        ProjProposalJour_DS.refresh();
    }

    // <GMY>
    if (TaxGSTFeatureChecker_MY::isCountryRegionMY())
    {
        if (ProjProposalJour.hasLinesWithReference_MY() && ProjProposalJour.hasLinesWithoutReference_MY())
        {
            if (!isWrnOutMY)
            {
                warning("@SYP4860678");
                isWrnOutMY = true;
            }
        }
        else
        {
            isWrnOutMY = false;
        }
    }
    // </GMY>
}

