public void write()
{
    EInvoiceAccountCode eInvoiceAccountCodeOrig;

    if (isCustPublic_NO)
    {
        eInvoiceAccountCodeOrig = projProposalRevenue.orig().EInvoiceAccountCode;
    }

    super();

    if (isCustPublic_NO && projProposalRevenue.EInvoiceAccountCode != eInvoiceAccountCodeOrig)
    {
        projProposalJour.setEInvoiceAccountCodeFromLines_NO();
        projProposalJour.update();

        ProjProposalJour_DS.reread();
        ProjProposalJour_DS.refresh();
    }

    // <GMY>
    if (TaxGSTFeatureChecker_MY::isCountryRegionMY())
    {
        if (ProjProposalJour.hasLinesWithReference_MY() && ProjProposalJour.hasLinesWithoutReference_MY())
        {
            if (!isWrnOutMY)
            {
                warning("@SYP4860678");
                isWrnOutMY = true;
            }
        }
        else
        {
            isWrnOutMY = false;
        }
    }
    // </GMY>
}

/// <summary>
/// Determines whetehr the current project transaction is retention release or not.
/// </summary>
/// <param name="_projTransId">
/// The <c>ProjTransIdBase</c> buffer to store the project transaction Id.
/// </param>
/// <returns>
/// True if retention released , otherwise false.
/// </returns>
public boolean isRetentionRelease(ProjTransIdBase   _projTransId)
{
    ProjRevenueTrans    projRevenueTransLoc;

    select RecId from projRevenueTransLoc
        where projRevenueTransLoc.IsReleased == NoYes::Yes &&
                projRevenueTransLoc.TransId    == _projTransId;

    return (projRevenueTransLoc ? true : false);
}

