void clicked()
{
    Args               args = new Args();

    if (!projProposalJour.ProposalId)
    {
        return;
    }

    super();

    args.caller(this);
    args.record(projProposalJour);

    if (ProjInvoiceCancel::main(args))
    {
        projProposalJour_ds.reread();

        projProposalJour_ds.refreshEx();

        element.setButtonAccess();
    }
}

