public void clicked()
{

    super();

    boolean yesButton = false;

    if (projProposalRevenue_ds.recordsMarked().lastIndex() >= 1)
    {
       yesButton = Box::yesNo("@Proj:DeleteAllMarkedRecords", DialogButton::Yes) == DialogButton::Yes;
    }
    else
    {
        yesButton = Box::yesNo(strFmt("@Proj:DeleteARecord", "@SYS55264"), DialogButton::Yes) == DialogButton::Yes;
    }

    PSAUnitsUpdateLog    psaUnitsUpdateLog;
    ProjProposalRevenue  projProposalRevenueGridItems;
    ProjProposalRevenue  proposalRevenue;

    if (yesButton)
    {
        ttsBegin;

        projProposalRevenueGridItems = projProposalRevenue_ds.getFirst(1, true);

        projProposalRevenueGridItems = projProposalRevenueGridItems ? projProposalRevenueGridItems : projProposalRevenue_DS.cursor();

        while (projProposalRevenueGridItems)
        {
            if (projProposalRevenueGridItems.PSAContractLineNum)
            {
                psaUnitsUpdateLog = PSAUnitsUpdateLog::find(projProposalRevenueGridItems.PSAContractLineNum, true);

                if (psaUnitsUpdateLog)
                {
                    psaUnitsUpdateLog.IsProposal = NoYes::No;
                    psaUnitsUpdateLog.update();
                }
            }

            select forUpdate proposalRevenue
                where proposalRevenue.RecId == projProposalRevenueGridItems.RecId;

            proposalRevenue.delete();

            projProposalRevenueGridItems = projProposalRevenue_ds.getNext();
        }

        ttsCommit;

        // <GTE>
        if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
        {
            element.updateDisplay(false);
        }
        else
        {
        // </GTE>
            element.updateDisplay(true, projProposalRevenue_DS.isRetentionRelease(projProposalRevenue.TransId));
        // <GTE>
        }
        // </GTE>

        projProposalRevenue_ds.research();
    }
}

