public void lookup()
{
    element.performCustInvoiceJourLookup_MY(this);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.text())
        {
            element.updateSourceRefControlsAfterLookup_MY(projProposalItem_SalesOrder);
        }
        else
        {
            projProposalItem_SalesOrder.RefInvoiceDate_MY = dateNull();
        }
    }

    return ret;
}

boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        projProposalJour.CurrencyId = ProjInvoiceTable::find(projProposalJour.ProjInvoiceProjId).CurrencyId;
    }

    return ret;
}

public void lookup()
{
    ReasonFormTable::reasonCodeLookup(
        this,
        LedgerJournalACType::Cust,
        LedgerJournalACType::Cust,
        true);
}

public boolean modified()
{
    boolean changed = super();

    if (changed)
    {
        // Needed since the change is actually on the ReasonTableRef table,
        // the ReasonTableRef (RecId) doesn't changes on update.
        projProposalJour_ds.forceWrite(true);
    }

    return changed;
}
