[Form]
/// <summary>
/// Displays Oh-hold, billable transactions not in the date range
/// and non-chargable transactions
/// </summary>
public class ProjInvoiceProposalPendingTrans extends FormRun
{
    ProjInvoiceProposalCreateLinesExt       proposalPendingLines;
    ProjInvoiceProposalCreateLinesParams    proposalParams;
    ProjInvoiceProposalCreateLinesParams    proposalCreateLinesParams;
    TransDate                               endDate;
    TransDate                               startDate;

    /// <summary>
    /// Initializes and sets the parameters into the class <c>ProjInvoiceProposalCreateLinesParams</c> parm methods
    /// </summary>
    const str includeSubProjects = "*";

}

/// <summary>
/// initializes parameters from the caller forms
/// </summary>
public void init()
{
    if (!element.args().caller())
    {
        throw error("@SYS22539");
    }

    super();

    proposalParams = element.args().caller().parmProposalParams();

    if (!proposalParams || !proposalParams.parmProjInvoiceProjId())
    {
        throw error("@SYP4861250");
    }

    if (element.args().menuItemName() == menuitemDisplayStr(ProjInvoiceProposalPendingTrans))
    {
        startDate = proposalParams.parmStartDate();
        endDate = proposalParams.parmEndDate() ? proposalParams.parmEndDate() : dateMax();
    }
    selectProjInvoiceProjId.text(proposalParams.parmProjInvoiceProjId());
    element.modifiedTransFilter_onhold();
}

/// <summary>
/// Selects and displays all the records which are of billable transactions
/// </summary>
void modifiedTransFilter_billable()
{
    this.createParams();

    proposalCreateLinesParams.parmProjInvPendingType(ProjInvPendingType::Billable);
    proposalPendingLines = ProjInvoiceProposalCreateLinesExt::newStandard(proposalCreateLinesParams.pack());
    proposalPendingLines.run();

    psaTmpProjProposalTransBillable.setTmpData(proposalPendingLines.getProposalTransDisplay());
    psaTmpProjProposalTransBillable_ds.executeQuery();
}

/// <summary>
/// Selects and displays all the records which are of on-hold transactions
/// </summary>
void modifiedTransFilter_onhold()
{
    this.createParams();

    proposalCreateLinesParams.parmProjInvPendingType(ProjInvPendingType::OnHold);

    proposalPendingLines = ProjInvoiceProposalCreateLinesExt::newStandard(proposalCreateLinesParams.pack());
    proposalPendingLines.run();

    psaTmpProjProposalTransOnhold.setTmpData(proposalPendingLines.getProposalTransDisplay());
    psaTmpProjProposalTransOnhold_ds.executeQuery();
}

/// <summary>
/// Selects and displays all the records which are of other transactions
/// </summary>
void modifiedTransFilter_other()
{
    this.createParams();

    proposalCreateLinesParams.parmProjInvPendingType(ProjInvPendingType::Other);
    proposalPendingLines = ProjInvoiceProposalCreateLinesExt::newStandard(proposalCreateLinesParams.pack());
    proposalPendingLines.run();

    psaTmpProjProposalTransOther.setTmpData(proposalPendingLines.getProposalTransDisplay());
    psaTmpProjProposalTransOther_ds.executeQuery();
}

/// <summary>
/// Clear dynalinks from Query
/// </summary>
public void init()
{
    super();

    this.query().dataSourceTable(tableNum(PSATmpProjProposalTrans)).clearDynalinks();
}

/// <summary>
/// Clear dynalinks from Query
/// </summary>
public void init()
{
    super();

    this.query().dataSourceTable(tableNum(PSATmpProjProposalTrans)).clearDynalinks();
}

/// <summary>
/// Calls modifiedTransFilter_other() method to show other transactions
/// </summary>
void clicked()
{
    element.modifiedTransFilter_onhold();
}

/// <summary>
/// Calls modifiedTransFilter_billable() method to show Billable transactions
/// </summary>
void clicked()
{
    element.modifiedTransFilter_billable();
}

/// <summary>
/// Calls modifiedTransFilter_other() method to show other transactions
/// </summary>
void clicked()
{
    element.modifiedTransFilter_other();
}
