/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    Set entitiesToExclude = new Set(Types::String);
    entitiesToExclude.add(dataentityviewstr(ProjCDSProjectContractEntity));

    Project::removeEntitiesFromOpenOfficeMenu(_menuOptions, entitiesToExclude);
}

public boolean canClose()
{
    boolean ret = true;

    ret = super();

    if (ret)
    {
        if (projInvoiceTable.ProjInvoiceProjId)
        {
            ret = projInvoiceTable.isContractValid();
        }
    }

    return ret;
}

public void close()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    super();
}

/// <summary>
/// Enable or disable form fields relating to identification depending on setup criteria
/// </summary>
public void enableDisableIdentificationFields()
{
    boolean lockFieldsForEditContracts = cdsLockingOptionForEditContracts && isContractExternallyManaged;

    ProjInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, ProjInvoiceProjId)).allowEdit(!lockFieldsForEditContracts);
    ProjInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, Description)).allowEdit(!lockFieldsForEditContracts);
    ProjInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, CurrencyId)).allowEdit(!lockFieldsForEditContracts);
    ProjInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, ProjLockSalesCurrency)).allowEdit(!lockFieldsForEditContracts);
    ProjInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, ForecastInvoiceFrequency)).allowEdit(!lockFieldsForEditContracts);
    ProjInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, ProjPriceGroup)).allowEdit(!lockFieldsForEditContracts);
}

public void enableDisableBillingRuleActions()
{
    if (PSAContractLineItems::hasBillingRules(projInvoiceTable.ProjInvoiceProjId))
    {
        manageContractStatus.enabled(true);
        psaProjProposalSelection.visible(false);
        psaProjProposalSelectionCLIN.visible(true);
    }
    else
    {
        manageContractStatus.enabled(false);
        psaProjProposalSelection.visible(true);
        psaProjProposalSelectionCLIN.visible(false);
    }

    boolean lockFieldsForCreateInvoiceProposalsContracts = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::CreateInvoiceProposals) && isContractExternallyManaged;
    boolean enableInvoiceProposal = ProjInvoiceTable::canCreateInvoiceProposal(projInvoiceTable.ProjInvoiceProjId);
    psaProjProposalSelection.enabled(enableInvoiceProposal && !lockFieldsForCreateInvoiceProposalsContracts);
    psaProjProposalSelectionCLIN.enabled(enableInvoiceProposal && !lockFieldsForCreateInvoiceProposalsContracts);
}

