public void enableDisableFundingLimitFields()
{
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryId)).allowEdit(true);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryGroupId)).allowEdit(true);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, Resource)).allowEdit(true);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ItemId)).allowEdit(true);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ActivityNumber)).allowEdit(true);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, FundingSource)).allowEdit(true);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ProjId)).allowEdit(true);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ValidFor)).allowEdit(true);

    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryId)).skip(false);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryGroupId)).skip(false);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, Resource)).skip(false);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ItemId)).skip(false);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ActivityNumber)).skip(false);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, FundingSource)).skip(false);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ProjId)).skip(false);
    projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ValidFor)).skip(false);

    if (!projFundingLimit.ProjId)
    {
        projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ActivityNumber)).allowEdit(false);
        projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ActivityNumber)).skip(true);
    }

    switch (projFundingLimit.ValidFor)
    {
        case ProjAllTrxType::All :
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryId)).allowEdit(false);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryGroupId)).allowEdit(false);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryId)).skip(true);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryGroupId)).skip(true);
            break;

        case ProjAllTrxType::Items :
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, Resource)).allowEdit(false);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, Resource)).skip(true);
            break;

        case ProjAllTrxType::Expenses :
        case ProjAllTrxType::Hours :
        case ProjAllTrxType::Fees :
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ItemId)).allowEdit(false);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ItemId)).skip(true);
            break;

        case ProjAllTrxType::Revenues :
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryId)).allowEdit(false);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryGroupId)).allowEdit(false);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryId)).skip(true);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, CategoryGroupId)).skip(true);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ItemId)).allowEdit(false);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, Resource)).allowEdit(false);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, Resource)).skip(true);
            projFundingLimit_ds.object(fieldNum(ProjFundingLimit, ItemId)).skip(true);
            break;
    }
}

