public void enableDisableFundingSourceFields()
{
    // Based on whether posted transactions exist
    if ((projFundingSource.RecId != 0) && projFundingSource.existsPostedTrx())
    {
        projFundingSource_ds.object(fieldNum(ProjFundingSource, FundingSourceId)).allowEdit(false);
        projFundingSource_ds.object(fieldNum(ProjFundingSource, FundingType)).allowEdit(false);
        editProjFunder.allowEdit(false);

        projFundingSource_ds.object(fieldNum(ProjFundingSource, FundingSourceId)).skip(true);
        projFundingSource_ds.object(fieldNum(ProjFundingSource, FundingType)).skip(true);
        editProjFunder.skip(true);
    }
    else
    {
        projFundingSource_ds.object(fieldNum(ProjFundingSource, FundingSourceId)).allowEdit(true);
        projFundingSource_ds.object(fieldNum(ProjFundingSource, FundingType)).allowEdit(true);
        editProjFunder.allowEdit(true);

        projFundingSource_ds.object(fieldNum(ProjFundingSource, FundingSourceId)).skip(false);
        projFundingSource_ds.object(fieldNum(ProjFundingSource, FundingType)).skip(false);
        editProjFunder.skip(false);

        if (!projFundingSource.RecId)
        {
            projFundingSource_FundingType.setFocus();
        }
   }

    projFundingSource_ds.object(fieldNum(ProjFundingSource, psaCustRetentionTermId)).allowEdit(projFundingSource.FundingType == ProjFundingType::Customer);

    // Based on Funding source type
    projFundingSource_ds.object(fieldNum(ProjFundingSource, LanguageId)).allowEdit(true);
    projFundingSource_ds.object(fieldNum(ProjFundingSource, InvoiceLocation)).allowEdit(true);

    projFundingSource_ds.object(fieldNum(ProjFundingSource, LanguageId)).skip(false);
    projFundingSource_ds.object(fieldNum(ProjFundingSource, InvoiceLocation)).skip(false);

    if (projFundingSource.FundingType == ProjFundingType::Organization ||
        projFundingSource.FundingType == ProjFundingType::OnHoldFundingSource)
    {
        projFundingSource_ds.object(fieldNum(ProjFundingSource, LanguageId)).allowEdit(false);
        projFundingSource_ds.object(fieldNum(ProjFundingSource, InvoiceLocation)).allowEdit(false);

        projFundingSource_ds.object(fieldNum(ProjFundingSource, LanguageId)).skip(true);
        projFundingSource_ds.object(fieldNum(ProjFundingSource, InvoiceLocation)).skip(true);
    }

    boolean lockFieldsForEditContracts = cdsLockingOptionForEditContracts && isContractExternallyManaged;

    AddFundingSource.enabled(!lockFieldsForEditContracts);
    ProjFundingSource_ds.allowCreate(!lockFieldsForEditContracts);

    RemoveFundingSource.enabled(!lockFieldsForEditContracts);
    ProjFundingSource_ds.allowDelete(!lockFieldsForEditContracts);
}

public void fillInvoiceFormatCombo()
{
    if (!isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        return;
    }

    projFundingSource_PSAInvoiceFormats.clear();
    if (PSAContractLineItems::hasBillingRules(projFundingSource.ContractId))
    {
        projFundingSource_PSAInvoiceFormats.add(enum2str(PSAInvoiceFormats::DetailInvoice));
    }
    else
    {
        projFundingSource_PSAInvoiceFormats.add(enum2str(PSAInvoiceFormats::DetailInvoice));
        projFundingSource_PSAInvoiceFormats.add(enum2str(PSAInvoiceFormats::SummaryInvoice));
        projFundingSource_PSAInvoiceFormats.add(enum2str(PSAInvoiceFormats::SummaryByCategory));
    }
}

    public void init()
    {
        if (ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled())
        {
            ProjInvoiceTable record = element.args().record();
            ProjectOperationsDWHelper dw = ProjectOperationsDWHelper::construct();
            dw.openProjectContractDetailsInCE(record.ProjInvoiceProjId, record.DataAreaId);
            this.close();
        }
        else
        {
            // <GIN>
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
            {
                isIndiaIndirectTaxParameterMarked   = TaxParameters::checkTaxParameters_IN();
                isIndiaWHTMarked                    = TaxWithholdParameters_IN::checkTaxParameters();
            }
            // </GIN>

        super();

		// Make the old adjustment form visble only when the feature key is disabled.
        Adjustment.visible(!ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled());

        ProjFundingLimit_LineNumber.visible(ProjParameters::find().EnableRecalculationFundingLimits);

        if (!isConfigurationkeyEnabled(configurationKeyNum(SMASubscription)))
        {
            smaAccrueSubscription.visible(false);
            smaAccrueSubscription.enabled(false);
        }

            if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
            {
                if (element.args().parm() == 'GoTo')
                {
                    projInvoiceTable_ds.positionToRecord(element.args().record());
                }
                element.fillInvoiceFormatCombo();
            }

            cdsLockingOptionForEditContracts = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::EditContracts);
            cdsLockingOptionForForecasts = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::Forecasts);
            cdsLockingOptionForAdjustments = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::Adjustments);

            // <GTE>
            if (TaxIntegrationUtils::isTaxInformationEnabled())
            {
                TransTaxInformation.visible(true);
            }
            // </GTE>

            ViewProjectcommittedDetails.visible(true);

            // If multiple contract lines per project feature is enabled
            // change labels and captions from "Billing rule" to "Contract line"
            if (element.enableMultipleContractLinesForProject)
            {
                BillingRules.caption("@PSA2825");
                PSAContractLineItems_ContractLineNum.label("@PSA2823");
                PSAContractLineItems_LineType.label("@SYS9844");
            }

            PSAContractLineItems_BillingMethod.enabled(element.enableMultipleContractLinesForProject);
            PSAContractLineItems_BillingMethod.visible(element.enableMultipleContractLinesForProject);

            PSAContractLineItems_IncludeHourTrans.enabled(element.enableMultipleContractLinesForProject);
            PSAContractLineItems_IncludeHourTrans.visible(element.enableMultipleContractLinesForProject);

            PSAContractLineItems_IncludeExpenseTrans.enabled(element.enableMultipleContractLinesForProject);
            PSAContractLineItems_IncludeExpenseTrans.visible(element.enableMultipleContractLinesForProject);

            PSAContractLineItems_IncludeFeeTrans.enabled(element.enableMultipleContractLinesForProject);
            PSAContractLineItems_IncludeFeeTrans.visible(element.enableMultipleContractLinesForProject);

            PSAContractLineItems_IncludeItemTrans.enabled(element.enableMultipleContractLinesForProject);
            PSAContractLineItems_IncludeItemTrans.visible(element.enableMultipleContractLinesForProject);
        }
    }

public   void refreshBillingRules()
{
    psaContractLineItems_ds.research();
    psaContractLineItems_ds.refresh();

    // Billing rules added or removed. Update Action pane
    element.enableDisableBillingRuleActions();
}

public void run()
{
    super();

    this.setTabPage();
}

public void setTabPage()
{
    if (element.args().caller() is FormRun)
    {
        FormRun formRun = element.args().caller();
        switch (formRun.name())
        {
            case formStr(ProjPosting):
                fundingSources.setFocus();
                break;

            case formStr(ProjGrantFundingDialog):
                fundingRules.activatePage();
                break;
        }
    }
}

public void switchBetwRetainMethod()
{
    if (!isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        return;
    }

    if (projInvoiceTable.psaRetainScheduleId)
    {
        projInvoiceTable.psaRetainPercent = 0;
        projInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, psaRetainPercent)).allowEdit(false);
    }
    else
    {
        projInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, psaRetainPercent)).allowEdit(true);
    }

    if (projInvoiceTable.psaRetainPercent)
    {
        projInvoiceTable.psaRetainScheduleId = "";
        projInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, psaRetainScheduleId)).allowEdit(false);
    }
    else
    {
        projInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, psaRetainScheduleId)).allowEdit(true);
    }
}

/// <summary>
/// Handles 'Select All' event.
/// </summary>
/// <param name="_taskId">
/// Id of the event.
/// </param>
/// <returns>
/// Id of the event.
/// </returns>
public int task(int _taskId)
{
    int ret;
    #Task

    ret = super(_taskId);

    if (ret)
    {
        switch (_taskId)
        {
            // Handle the browse buttons in the form
            case #taskNextRecord:
            case #taskPrevRecord:
            case #taskCtrlHome :
            case #taskCtrlEnd :
                if (projInvoiceTable.ProjInvoiceProjId)
                {
                    ret = projInvoiceTable.isContractValid();
                }
                break;
        }
    }

    return ret;
}

public void create(boolean _append = false)
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(ProjParameters::numRefProjInvoiceProjId().NumberSequenceId,
                                                             element,
                                                             projInvoiceTable_ds,
                                                             fieldNum(ProjInvoiceTable, ProjInvoiceProjId));

        if (!numberSeqFormHandler.parmNumberSequenceId())
        {
            numberSeqFormHandler = null;
            throw error(strFmt("@SYS26165","@SYS80725"));
        }
    }

    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodDataSourceCreatePre();
    }

    super(_append);
}

