public void init()
{
    ProjGrantView projGrantViewCaller;

    super();

    if (element.args().dataset() == tableNum(ProjGrantView) && element.args().caller().name() == formStr(ProjGrant))
    {
        projGrantViewCaller = element.args().record() as ProjGrantView;

        if (projGrantViewCaller.ProjInvoiceProjId == "")
        {
            this.query().dataSourceTable(tableNum(ProjInvoiceTable)).clearDynalinks();
        }
    }
}

