    }
}

public int active()
{
    int ret;

    ret = super();

    nextPriority = 1;

    //only one of 2 alternatives enabled for retainage
    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        element.switchBetwRetainMethod();
    }

    isContractExternallyManaged = Project::isContractExternallyManaged(ProjInvoiceTable.ProjInvoiceProjId);

    element.enableDisableIdentificationFields();
    element.enableDisableBillingRuleActions();

    boolean lockFieldsForEditContracts = cdsLockingOptionForEditContracts && isContractExternallyManaged;
    boolean lockFieldsForForecasts = cdsLockingOptionForForecasts && isContractExternallyManaged;
    boolean lockFieldsForAdjustments = cdsLockingOptionForAdjustments && isContractExternallyManaged;

    AddBillingRule.enabled(!lockFieldsForEditContracts);
    PSAContractLineItems_ds.allowCreate(!lockFieldsForEditContracts);
    RemoveBillingRule.enabled(!lockFieldsForEditContracts);
    PSAContractLineItems_ds.allowDelete(!lockFieldsForEditContracts);

    AddFundingLimit.enabled(!lockFieldsForEditContracts);
    RemoveFundingLimit.enabled(!lockFieldsForEditContracts);
    ProjFundingLimit_ds.allowEdit(!lockFieldsForEditContracts);

    AddFundingRule.enabled(!lockFieldsForEditContracts);
    RemoveFundingRule.enabled(!lockFieldsForEditContracts);
    RuleAllocation.enabled(!lockFieldsForEditContracts);
    ProjFundingRule_ds.allowEdit(!lockFieldsForEditContracts);

    AddFundingSource.enabled(!lockFieldsForEditContracts);
    ProjFundingSource_ds.allowCreate(!lockFieldsForEditContracts);
    RemoveFundingSource.enabled(!lockFieldsForEditContracts);
    ProjFundingSource_ds.allowDelete(!lockFieldsForEditContracts);

    ProjForecastEmpl.enabled(!lockFieldsForForecasts);
    ProjForecastCost.enabled(!lockFieldsForForecasts);
    ForecastSalesProj.enabled(!lockFieldsForForecasts);
    ProjForecastRevenue.enabled(!lockFieldsForForecasts);
    ProjForecastOnAcc.enabled(!lockFieldsForForecasts);
    ProjForecastBudgetCopy.enabled(!lockFieldsForForecasts);
    ProjForecastBudgetDelete.enabled(!lockFieldsForForecasts);

    Adjustment.enabled(!lockFieldsForAdjustments);

     if (!ProjInvoiceTable::hasExternalFundingSources(projInvoiceTable.ProjInvoiceProjId))
    {
        CtrlSalesOrders.enabled(false);
    }
    else
    {
        CtrlSalesOrders.enabled(true);
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodDataSourceValidateWrite();
    }

    if (projInvoiceTable.orig().CurrencyId && projInvoiceTable.CurrencyId != projInvoiceTable.orig().CurrencyId)
    {
        currencyChanged = true;
        if (projInvoiceTable.existOpenProjOnAcc())
        {
            convertCurrency = ProjInvoiceTable::promptConvertCurrencyCodeProj();
        }
    }

    return ret;
}

public void write()
{
    ProjTable   projTable;

    ttsbegin;

    if (convertCurrency)
    {
        projInvoiceTable.convertCurrencyCodeProj();
        convertCurrency = false;
    }

    super();

    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodDataSourceWrite();
    }

    ttscommit;

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        select firstonly RecId, ProjInvoiceProjId from projTable where projTable.ProjInvoiceProjId == projInvoiceTable.ProjInvoiceProjId;
        if (currencyChanged && projTable.RecId)
        {
            info("@SYS138556");
            currencyChanged = false;
        }
    }
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;

    // Do not need to check funding type as this can only be a customer for this location
    CustTable custTable = CustTable::find(ProjFundingSource::findCustAccount(projInvoiceTable.ProjInvoiceProjId).CustAccount);

    ret = LogisticsLocationSelectionLookup::lookupAddressForm(_formReferenceControl, custTable,
        [LogisticsLocationRoleType::Invoice], true);

    return ret;
}

