        projInvoiceTable_ds.refresh();
    }
}

public void modified()
{
    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        element.switchBetwRetainMethod();
    }
}

public void write()
{
    super();

    if (projFundingSource.FundingType != ProjFundingType::OnHoldFundingSource)
    {
        if (ProjFundingSource::numberOfFundingSources(projInvoiceTable.ProjInvoiceProjId) == 1)
        {
            if (ProjFundingRule::numberOfRulesForContract(projInvoiceTable.ProjInvoiceProjId) == 0)
            {
                if (Box::yesNo("@SYS320044", DialogButton::Yes) == DialogButton::Yes)
                {
                    ProjFundingRule::createDefaultFundingRule(projInvoiceTable.ProjInvoiceProjId, projFundingSource.RecId);
                    projFundingRule_ds.research();
                }
            }
        }
    }
}

