private boolean fundingLimitAttributesChanged()
{
    boolean attributesChanged = false;
    if (projFundingLimit.ActivityNumber != projFundingLimit.orig().ActivityNumber ||
                projFundingLimit.CategoryGroupId != projFundingLimit.orig().CategoryGroupId ||
                projFundingLimit.CategoryId != projFundingLimit.orig().CategoryId ||
                projFundingLimit.FundingSource != projFundingLimit.orig().FundingSource ||
                projFundingLimit.ItemId != projFundingLimit.orig().ItemId ||
                projFundingLimit.ProjId != projFundingLimit.orig().ProjId ||
                projFundingLimit.Resource != projFundingLimit.orig().Resource ||
                projFundingLimit.ValidFor != projFundingLimit.orig().ValidFor )
    {
        attributesChanged = true;
    }
    return attributesChanged;
}

public boolean validateWrite()
{
    boolean ret = super();

    if (ret)
    {
        //
        // When creating a new record the fundingLimitAttributesChanged() will return true. Once the record is saved, these
        // attribute are locked and should not be allowed to changed.
        //
        if (this.fundingLimitAttributesChanged()
            || (projFundingLimit.AmountMst != projFundingLimit.orig().AmountMst && projFundingLimit.orig().AmountMst == 0.00))
        {
            ProjComputeFundingLimitActualAmount projComputeFundingLimitActualAmount = ProjComputeFundingLimitActualAmount::construct(projFundingLimit);
            AmountCur actualsAmount = ProjComputeFundingLimitActualAmount.run();

            if (actualsAmount != projFundingLimit.LimitSpent)
            {
                if (actualsAmount > projFundingLimit.AmountMst)
                {
                    Box::Info(strFmt("@Proj:ComputedAmountExceedsLimit", actualsAmount - ProjFundingLimit.AmountMst));
                    ret = false;
                }
                else
                {
                    Box::Info(strFmt("@Proj:LimitUpdateWithComputedAmount", actualsAmount));
                    projFundingLimit.LimitSpent = actualsAmount;
                }
            }
        }
    }

    return ret;
}

