public void write(boolean _flag = false)
{
    boolean newLocation =false;

    if (_flag)
    {
        newLocation = logisticsLocationHeader.RecId ? false : true;

        super();

        if (newLocation)
        {
            DirPartyLocationContract partyLocationContract = DirPartyLocationContract::constructForPartyLocation(
                CustTable::find(ProjFundingSource::findCustAccount(projInvoiceTable.ProjInvoiceProjId).CustAccount).Party, 
                logisticsLocationHeader.RecId);
            partyLocationContract.parmIsPostalAddress(true);
            partyLocationContract.parmIsPrimary(false);
            partyLocationContract.parmIsPrivate(false);
            partyLocationContract.parmRoleIds([LogisticsLocationRole::findBytype(LogisticsLocationRoleType::Invoice).RecId]);

            if (!DirParty::createOrUpdateLocation(partyLocationContract))
            {
                throw error("@SYS136235");
            }
        }
    }
    else
    {
        logisticsPostalAddressHeader_ds.write();
    }

    logisticsLocationHeader_ds.forceWrite(false);
}

public void modified()
{
    CustTable                       custTable;
    DirPartyLocation                partyLocation;
    LogisticsLocation               location;
    LogisticsPostalAddress          postalAddress;

    if (!logisticsLocationHeader.RecId ||
        logisticsLocationHeader.LocationId != LogisticsLocation::find(logisticsLocationHeader.RecId).LocationId
       )
    {
        location = LogisticsLocation::findByLocationId(logisticsLocationHeader.LocationId);
        if (location)
        {
            select AccountNum, Party from custTable
                where custTable.AccountNum == ProjFundingSource::findCustAccount(projInvoiceTable.ProjInvoiceProjId).CustAccount
                join partyLocation
                    where partyLocation.Party == custTable.Party &&
                          partyLocation.Location == location.RecId
                join postalAddress
                    where postalAddress.Location == location.RecId;

            if (partyLocation)
            {
                logisticsLocationHeader.data(location);
                logisticsPostalAddressHeader.data(postalAddress);
                logisticsPostalAddressHeader_ds.refresh();
                logisticsLocationHeader_ds.refresh();
                logisticsPostalAddressHeader_ds.refresh();
                projInvoiceTable_ds.forceWrite(true);
                logisticsLocationHeader_ds.forceWrite(true);
            }
        }
        if (!location || !partyLocation)
        {
            if (!logisticsLocationHeader.LocationId || Box::okCancel("@SYS136234",DialogButton::Ok) == DialogButton::Ok)
            {
                location.LocationId = logisticsLocationHeader.LocationId;
                location.Description = logisticsLocationHeader.Description;
                logisticsLocationHeader.data(location);

                super();

                logisticsPostalAddressHeader.data(postalAddress);
            }
            else
            {
                logisticsLocationHeader_Ds.reread();
            }
        }
    }
    else
    {
        super();
    }
}

