public int active()
{
    int ret;

    ret = super();

    if (psaContractLineItems.hasPostedHistory() || (element.enableMultipleContractLinesForProject && psaContractLineItems.BillingMethod == ProjContractBillingMethod::FixedPrice && psaContractLineItems.hasEstimates()))
    {
        psaContractLineItems_ds.allowDelete(false);
    }
    else
    {
        boolean lockFieldsForEditContracts = cdsLockingOptionForEditContracts && isContractExternallyManaged;

        psaContractLineItems_ds.allowDelete(!lockFieldsForEditContracts);
    }

    return ret;
}

public void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();

    if (projInvoiceTable_ds.cursor() == null)
    {
        return;
    }

    super();

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Project, PrintMgmtNodeType::ProjInvoiceTable);
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(projInvoiceTable_ds.cursor());
    setupContext.parmCountryRegionId(LogisticsPostalAddress::findByLocation(projInvoiceTable.InvoiceLocation).CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

